import java.util.*;

abstract class Vehicle {
    // abstract method to be implemented by subclasses
    abstract double getServiceCost();

    // concrete method, just to show that abstract class can have body
    int getDimension() {
        return 2;
    }
}

interface Describable {
    void describe();
}

// ---------- Car ----------
class Car extends Vehicle implements Describable {
    String brand;
    boolean member;

    Car(String brand, boolean member) {
        this.brand = brand;
        this.member = member;
    }

    @Override
    double getServiceCost() {
        double base = 500_000;
        if (member) {
            base *= 0.9;          // 10% discount for members
        }
        return base;
    }

    @Override
    public void describe() {
        System.out.println("Car " + brand + (member ? " (Member)" : ""));
    }
}

// ---------- Motorcycle ----------
class Motorcycle extends Vehicle implements Describable {
    String brand;
    boolean member;

    Motorcycle(String brand, boolean member) {
        this.brand = brand;
        this.member = member;
    }

    @Override
    double getServiceCost() {
        double base = 300_000;
        if (member) {
            base *= 0.9;          // 10% discount for members
        }
        return base;
    }

    @Override
    public void describe() {
        System.out.println("Motorcycle " + brand + (member ? " (Member)" : ""));
    }
}

// ---------- Truck ----------
class Truck extends Vehicle implements Describable {
    String brand;
    boolean member;

    Truck(String brand, boolean member) {
        this.brand = brand;
        this.member = member;
    }

    @Override
    double getServiceCost() {
        double base = 800_000;
        if (member) {
            base *= 0.85;         // 15% discount for members
        }
        return base;
    }

    @Override
    public void describe() {
        System.out.println("Truck " + brand + (member ? " (Member)" : ""));
    }
}

// ---------- Main Program ----------
public class ServiceCenter {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        System.out.print("Jumlah pelanggan: ");
        int n = sc.nextInt();
        sc.nextLine();                     // consume newline

        double total = 0;

        for (int i = 0; i < n; i++) {
            System.out.println("Pelanggan ke-" + (i + 1));

            // ---- validate vehicle type with while loop ----
            String type;
            while (true) {
                System.out.print("Jenis kendaraan (Car/Motorcycle/Truck): ");
                type = sc.nextLine().trim();
                if (type.equalsIgnoreCase("Car") ||
                    type.equalsIgnoreCase("Motorcycle") ||
                    type.equalsIgnoreCase("Truck")) {
                    break;
                }
                System.out.println("Jenis tidak dikenal, coba lagi.");
            }

            System.out.print("Merek: ");
            String brand = sc.nextLine().trim();

            System.out.print("Member? (Y/N): ");
            char memChar = sc.nextLine().trim().toUpperCase().charAt(0);
            boolean isMember = memChar == 'Y';

            // ---- instantiate appropriate class ----
            Describable vehicle;
            if (type.equalsIgnoreCase("Car")) {
                vehicle = new Car(brand, isMember);
            } else if (type.equalsIgnoreCase("Motorcycle")) {
                vehicle = new Motorcycle(brand, isMember);
            } else {
                vehicle = new Truck(brand, isMember);
            }

            // ---- output description and cost ----
            vehicle.describe();
            double cost = ((Vehicle) vehicle).getServiceCost();
            System.out.println("Biaya layanan: Rp " + cost);
            total += cost;
        }

        System.out.println("Total semua pelanggan: Rp " + total);
    }
}
