// ------------------------------------------------------------
// Abstract class Vehicle
// ------------------------------------------------------------
abstract class Vehicle {
    protected String name;
    protected int maxSpeed; // km/h

    public Vehicle(String name, int maxSpeed) {
        this.name = name;
        this.maxSpeed = maxSpeed;
    }

    // abstract method: must be implemented by subclasses
    public abstract String getVehicleType();

    // concrete method: can be used by all subclasses
    public void displayInfo() {
        System.out.println("=== Vehicle Info ===");
        System.out.println("Name: " + name);
        System.out.println("Type: " + getVehicleType());
        System.out.println("Max Speed: " + maxSpeed + " km/h");
    }
}

// ------------------------------------------------------------
// Interface CargoCarrier
// ------------------------------------------------------------
interface CargoCarrier {
    int MAX_CARGO_WEIGHT = 10000; // public static final by default

    void loadCargo(int weight); // abstract method

    // default method (Java 8+)
    default int getCargoCapacity() {
        return MAX_CARGO_WEIGHT;
    }
}

// ------------------------------------------------------------
// Interface PassengerCarrier
// ------------------------------------------------------------
interface PassengerCarrier {
    int MAX_PASSENGERS = 200; // public static final by default

    void boardPassenger(int count); // abstract method

    // default method (Java 8+)
    default int getPassengerCapacity() {
        return MAX_PASSENGERS;
    }
}

// ------------------------------------------------------------
// Concrete class Airplane (only cargo)
// ------------------------------------------------------------
class Airplane extends Vehicle implements CargoCarrier {

    public Airplane(String name, int maxSpeed) {
        super(name, maxSpeed);
    }

    @Override
    public String getVehicleType() {
        return "Airplane";
    }

    @Override
    public void loadCargo(int weight) {
        System.out.println("Loading " + weight + " kg cargo.");
    }
}

// ------------------------------------------------------------
// Concrete class Ship (cargo + passenger)
// ------------------------------------------------------------
class Ship extends Vehicle implements CargoCarrier, PassengerCarrier {

    public Ship(String name, int maxSpeed) {
        super(name, maxSpeed);
    }

    @Override
    public String getVehicleType() {
        return "Ship";
    }

    @Override
    public void loadCargo(int weight) {
        System.out.println("Loading " + weight + " kg cargo.");
    }

    @Override
    public void boardPassenger(int count) {
        System.out.println("Boarding " + count + " passengers.");
    }
}

// ------------------------------------------------------------
// Concrete class Car (only passenger)
// ------------------------------------------------------------
class Car extends Vehicle implements PassengerCarrier {

    public Car(String name, int maxSpeed) {
        super(name, maxSpeed);
    }

    @Override
    public String getVehicleType() {
        return "Car";
    }

    @Override
    public void boardPassenger(int count) {
        System.out.println("Boarding " + count + " passengers.");
    }
}

// ------------------------------------------------------------
// Main class
// ------------------------------------------------------------
public class TransportSystem {
    public static void main(String[] args) {
        // Airplane instance
        Airplane plane = new Airplane("Eagle One", 900);
        plane.displayInfo();
        plane.loadCargo(5000);
        System.out.println("Cargo capacity (default): " + plane.getCargoCapacity() + " kg");
        System.out.println();

        // Ship instance
        Ship ship = new Ship("Oceanic Voyager", 55);
        ship.displayInfo();
        ship.loadCargo(8000);
        ship.boardPassenger(150);
        System.out.println("Cargo capacity (default): " + ship.getCargoCapacity() + " kg");
        System.out.println("Passenger capacity (default): " + ship.getPassengerCapacity());
        System.out.println();

        // Car instance
        Car car = new Car("City Runner", 180);
        car.displayInfo();
        car.boardPassenger(4);
        System.out.println("Passenger capacity (default): " + car.getPassengerCapacity());
    }
}
