        System.out.println("2. Kucing");
        System.out.print("Pilihan Anda: ");
        int pilihan = sc.nextInt();

        Animal hewan;   // reference variable of superclass type

        if (pilihan == 1) {
            hewan = new Dog();   // refer to Dog object
        } else if (pilihan == 2) {
            hewan = new Cat();   // refer to Cat object
        } else {
            hewan = new Animal(); // fallback
        }

        hewan.makeSound(); // polymorphic call
        sc.close();
    }
}
