import java.util.Scanner;

class Animal {
    void makeSound() {
        System.out.print("Suara hewan: ");
    }
}

class Dog extends Animal {
    @Override
    void makeSound() {
        super.makeSound();
        System.out.println("Guk Guk");
    }
}

class Cat extends Animal {
    @Override
    void makeSound() {
        super.makeSound();
        System.out.println("Meong");
    }
}

class Bird extends Animal {
    @Override
    void makeSound() {
        super.makeSound();
        System.out.println("Cuit");
    }
}

public class PetShop {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        System.out.println("=== Toko Hewan Pak Budi ===");
        while (true) {
            System.out.print("Pilih hewan (1=Dog, 2=Cat, 3=Bird, 0=Keluar): ");
            int choice = sc.nextInt();
            if (choice == 0) {
                System.out.println("Terima kasih, silakan datang kembali!");
                break;
            }
            System.out.print("Berapa kali suara? ");
            int times = sc.nextInt();

            Animal a;
            if (choice == 1) {
                a = new Dog();
            } else if (choice == 2) {
                a = new Cat();
            } else if (choice == 3) {
                a = new Bird();
            } else {
                System.out.println("Pilihan tidak valid.");
                continue;
            }

            for (int i = 0; i < times; i++) {
                a.makeSound();
            }
            System.out.println();
        }
        sc.close();
    }
}
```

