import java.util.Scanner;

class Anggota {
    private String nama;
    private String jenisPinjaman;
    private double jumlahPinjaman;

    public Anggota(String nama, String jenisPinjaman, double jumlahPinjaman) {
        this.nama = nama;
        this.jenisPinjaman = jenisPinjaman;
        this.jumlahPinjaman = jumlahPinjaman;
    }

    public String getNama() {
        return nama;
    }

    public String getJenisPinjaman() {
        return jenisPinjaman;
    }

    public double getJumlahPinjaman() {
        return jumlahPinjaman;
    }

    public void setJumlahPinjaman(double jumlahPinjaman) {
        this.jumlahPinjaman = jumlahPinjaman;
    }
}

class Pinjaman {
    private Anggota anggota;

    public Pinjaman(Anggota anggota) {
        this.anggota = anggota;
    }

    // menghitung persentase bunga per bulan
    public double hitungBunga() {
        double dasar;
        String jenis = anggota.getJenisPinjaman().toLowerCase();
        switch (jenis) {
            case "konsumsi":
                dasar = 2.0;
                break;
            case "usaha":
                dasar = 1.5;
                break;
            case "pendidikan":
                dasar = 1.0;
                break;
            default:
                dasar = 0.0; // tidak akan terjadi karena validasi sebelumnya
        }
        if (anggota.getJumlahPinjaman() > 1_000_000) {
            dasar += 0.5;
        }
        return dasar;
    }

    public void tampilRincian() {
        System.out.println("Rincian Pinjaman:");
        System.out.println("Nama          : " + anggota.getNama());
        System.out.println("Jenis Pinjaman: " + anggota.getJenisPinjaman());
        System.out.printf("Jumlah Pinjaman: Rp%,.0f%n", anggota.getJumlahPinjaman());
        System.out.printf("Bunga per Bulan : %.1f%%%n", hitungBunga());
    }
}

public class KoperasiSejahtera {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        Anggota[] daftar = new Anggota[3];

        for (int i = 0; i < 3; i++) {
            System.out.println("Masukkan data anggota ke-" + (i + 1) + ":");
            System.out.print("Nama: ");
            String nama = sc.nextLine();

            System.out.print("Jenis pinjaman (Konsumsi/Usaha/Pendidikan): ");
            String jenis = sc.nextLine();

            double jumlah = 0;
            boolean valid = false;
            while (!valid) {
                System.out.print("Jumlah pinjaman (Rp): ");
                String input = sc.nextLine();
                try {
                    jumlah = Double.parseDouble(input);
                    if (jumlah <= 0) {
                        System.out.println("Input tidak valid! Jumlah pinjaman harus positif.");
                    } else {
                        valid = true;
                    }
                } catch (NumberFormatException e) {
                    System.out.println("Input tidak valid! Harap masukkan angka.");
                }
            }

            daftar[i] = new Anggota(nama, jenis, jumlah);
            System.out.println("----------------------------------------");
            Pinjaman p = new Pinjaman(daftar[i]);
            p.tampilRincian();
            System.out.println();
        }
        sc.close();
    }
}
