```java
package com.example.kedaibudi;

import android.app.Activity;
import android.os.Bundle;
import android.util.Log;
import android.view.View;
import android.widget.Button;
import android.widget.TextView;
import java.util.Calendar;

public class MainActivity extends Activity {

    private static final String TAG = "MainActivity";
    private TextView tvMessage;
    private Button btnShowPromo;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        Log.d(TAG, "onCreate() dipanggil");
        setContentView(R.layout.activity_main);

        tvMessage = findViewById(R.id.tvMessage);
        btnShowPromo = findViewById(R.id.btnShowPromo);

        tvMessage.setText("Selamat datang di Kedai Budi");

        btnShowPromo.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View v) {
                showPromoBasedOnTime();
            }
        });
    }

    @Override
    protected void onStart() {
        super.onStart();
        Log.d(TAG, "onStart() dipanggil");
    }

    @Override
    protected void onResume() {
        super.onResume();
        Log.d(TAG, "onResume() dipanggil");
        btnShowPromo.setEnabled(true);
    }

    @Override
    protected void onPause() {
        super.onPause();
        Log.d(TAG, "onPause() dipanggil");
        btnShowPromo.setEnabled(false);
    }

    private void showPromoBasedOnTime() {
        Calendar calendar = Calendar.getInstance();
        int hour = calendar.get(Calendar.HOUR_OF_DAY);

        if (hour >= 6 && hour < 12) {
            tvMessage.setText("Pagi Ceria: Diskon 10% untuk semua kopi");
        } else if (hour >= 12 && hour < 18) {
            tvMessage.setText("Siang Santai: Beli 1 Gratis 1 untuk pastry");
        } else if (hour >= 18 && hour < 24) {
            tvMessage.setText("Malam Hangat: Gratis topping pada setiap minuman");
        } else {
            tvMessage.setText("Kedai Budi tutup, silakan kembali besok");
        }
    }
}
```

