package com.example.hitungkedatangan;

import android.os.Bundle;
import android.text.TextUtils;
import android.view.KeyEvent;
import android.view.inputmethod.EditorInfo;
import android.widget.Button;
import android.widget.EditText;
import android.widget.ScrollView;
import android.widget.TextView;
import androidx.appcompat.app.AppCompatActivity;

public class MainActivity extends AppCompatActivity {

    private TextView tvCounter;
    private TextView tvLog;
    private EditText etName;
    private Button btnReset;

    // counter untuk jumlah kali onResume dipanggil
    private int resumeCount = 0;
    // StringBuilder untuk menampung log lifecycle
    private StringBuilder logBuilder = new StringBuilder();

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_main);
        tvCounter = findViewById(R.id.tvCounter);
        tvLog = findViewById(R.id.tvLog);
        etName = findViewById(R.id.etName);
        btnReset = findViewById(R.id.btnReset);

        appendLog("onCreate()");
        updateCounter();

        // aksi tombol Reset
        btnReset.setOnClickListener(v -> {
            resumeCount = 0;
            logBuilder.setLength(0);
            appendLog("Log direset");
            updateCounter();
            tvLog.setText(logBuilder.toString());
        });

        // aksi ketika user menekan Enter pada keyboard
        etName.setOnEditorActionListener((v, actionId, event) -> {
            boolean handled = false;
            if (actionId == EditorInfo.IME_ACTION_DONE ||
                (event != null && event.getKeyCode() == KeyEvent.KEYCODE_ENTER
                 && event.getAction() == KeyEvent.ACTION_DOWN)) {
                String name = etName.getText().toString().trim();
                if (!TextUtils.isEmpty(name)) {
                    appendLog("Pelanggan: " + name);
                    tvLog.setText(logBuilder.toString());
                    etName.setText("");
                }
                handled = true;
            }
            return handled;
        });
    }

    @Override
    protected void onStart() {
        super.onStart();
        appendLog("onStart()");
    }

    @Override
    protected void onResume() {
        super.onResume();
        resumeCount++;
        appendLog("onResume()");
        updateCounter();
    }

    @Override
    protected void onPause() {
        super.onPause();
        appendLog("onPause()");
    }

    @Override
    protected void onStop() {
        super.onStop();
        appendLog("onStop()");
    }

    @Override
    protected void onRestart() {
        super.onRestart();
        appendLog("onRestart()");
    }

    @Override
    protected void onDestroy() {
        super.onDestroy();
        appendLog("onDestroy()");
    }

    // menambahkan teks ke log dan menampilkan pada TextView
    private void appendLog(String text) {
        logBuilder.append(text).append("\n");
        tvLog.setText(logBuilder.toString());
        // scroll otomatis ke bagian bawah
        final ScrollView sv = findViewById(R.id.svLog);
        sv.post(() -> sv.fullScroll(ScrollView.FOCUS_DOWN));
    }

    // memperbarui tampilan counter kedatangan
    private void updateCounter() {
        tvCounter.setText("Kedatangan: " + resumeCount);
    }
}
