```java (android)
package com.example.kopinusantara;

import android.app.Activity;
import android.os.Bundle;
import android.view.View;
import android.widget.Button;
import android.widget.TextView;
import java.util.Stack;

public class MainActivity extends Activity {

    private TextView tvLog, tvScreen;
    private Stack<String> screenStack = new Stack<>();

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_main);
        tvLog = findViewById(R.id.tvLog);
        tvScreen = findViewById(R.id.tvScreen);

        log("[onCreate] Activity dibuat");

        Button btnMenu = findViewById(R.id.btnMenu);
        Button btnOrder = findViewById(R.id.btnOrder);
        Button btnHistory = findViewById(R.id.btnHistory);
        Button btnBack = findViewById(R.id.btnBack);

        btnMenu.setOnClickListener(new View.OnClickListener() {
            @Override public void onClick(View v) {
                pushScreen("Menu");
            }
        });
        btnOrder.setOnClickListener(new View.OnClickListener() {
            @Override public void onClick(View v) {
                pushScreen("Order");
            }
        });
        btnHistory.setOnClickListener(new View.OnClickListener() {
            @Override public void onClick(View v) {
                pushScreen("History");
            }
        });
        btnBack.setOnClickListener(new View.OnClickListener() {
            @Override public void onClick(View v) {
                popScreen();
            }
        });
    }

    private void pushScreen(String name) {
        screenStack.push(name);
        log("[Action] Buka " + name + " – push");
        tvScreen.setText("Layar aktif: " + name);
    }

    private void popScreen() {
        if (!screenStack.isEmpty()) {
            String removed = screenStack.pop();
            log("[Action] Kembali – pop (" + removed + ")");
        }
        if (!screenStack.isEmpty()) {
            tvScreen.setText("Layar aktif: " + screenStack.peek());
        } else {
            tvScreen.setText("Tidak ada layar aktif");
        }
    }

    private void log(String message) {
        tvLog.append(message + "\n");
    }

    @Override
    protected void onStart() {
        super.onStart();
        log("[onStart] Activity siap ditampilkan");
    }

    @Override
    protected void onResume() {
        super.onResume();
        log("[onResume] Activity menjadi aktif");
    }

    @Override
    protected void onPause() {
        super.onPause();
        log("[onPause] Activity kehilangan fokus");
    }

    @Override
    protected void onStop() {
        super.onStop();
        log("[onStop] Activity tidak terlihat");
    }

    @Override
    protected void onRestart() {
        super.onRestart();
        log("[onRestart] Activity akan kembali");
    }

    @Override
    protected void onDestroy() {
        super.onDestroy();
        log("[onDestroy] Activity dihancurkan");
    }
}
```

