package com.example.kopiceria;

import android.os.Bundle;
import android.os.Handler;
import android.os.Looper;
import android.view.View;
import android.widget.Button;
import android.widget.ProgressBar;
import android.widget.TextView;
import androidx.appcompat.app.AppCompatActivity;

public class MainActivity extends AppCompatActivity {

    private Button btnStart;
    private TextView tvStatus;
    private ProgressBar progressBar;
    private Handler uiHandler = new Handler(Looper.getMainLooper());

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_main);

        btnStart   = findViewById(R.id.btnStart);
        tvStatus   = findViewById(R.id.tvStatus);
        progressBar = findViewById(R.id.progressBar);
        progressBar.setVisibility(View.INVISIBLE);

        btnStart.setOnClickListener(v -> startBrewing());
    }

    private void startBrewing() {
        btnStart.setEnabled(false);
        progressBar.setVisibility(View.VISIBLE);
        tvStatus.setText("Menyeduh… (detik ke-1)");

        // Background thread untuk simulasi proses penyeduhan
        new Thread(() -> {
            for (int i = 1; i <= 5; i++) {
                try {
                    Thread.sleep(1000);               // jeda 1 detik
                } catch (InterruptedException e) {
                    e.printStackTrace();
                }

                final int second = i;
                // Kirim update ke UI thread
                uiHandler.post(() -> {
                    if (second < 5) {
                        tvStatus.setText("Menyeduh… (detik ke-" + (second + 1) + ")");
                    } else {
                        // Proses selesai
                        progressBar.setVisibility(View.INVISIBLE);
                        tvStatus.setText("Kopi siap!");
                        btnStart.setEnabled(true);
                    }
                });
            }
        }).start();
    }
}
```

