```java
// MainActivity.java
package com.example.cucicepat;

import android.content.Intent;
import android.os.Bundle;
import android.os.Handler;
import android.os.Looper;
import android.view.View;
import android.widget.*;
import androidx.annotation.Nullable;
import androidx.appcompat.app.AppCompatActivity;
import androidx.recyclerview.widget.*;

import java.util.*;

public class MainActivity extends AppCompatActivity {

    private EditText edtCount, edtType;
    private Button btnProcess;
    private RecyclerView rvOrders;
    private OrderAdapter adapter;
    private final List<Order> orderList = new ArrayList<>();
    private final Handler uiHandler = new Handler(Looper.getMainLooper());

    @Override
    protected void onCreate(@Nullable Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_main);

        edtCount = findViewById(R.id.edtCount);
        edtType  = findViewById(R.id.edtType);
        btnProcess = findViewById(R.id.btnProcess);
        rvOrders = findViewById(R.id.rvOrders);

        adapter = new OrderAdapter(orderList, this::openDetail);
        rvOrders.setLayoutManager(new LinearLayoutManager(this));
        rvOrders.setAdapter(adapter);

        btnProcess.setOnClickListener(v -> startBackgroundCalculation());
    }

    private void startBackgroundCalculation() {
        final int count = Integer.parseInt(edtCount.getText().toString().trim());
        final String type = edtType.getText().toString().trim();

        // Clear previous data
        orderList.clear();
        adapter.notifyDataSetChanged();

        // Background thread
        new Thread(() -> {
            Random rnd = new Random();
            for (int i = 1; i <= count; i++) {
                try { Thread.sleep(2000); } catch (InterruptedException ignored) {}

                int weight = rnd.nextInt(5) + 1; // 1‑5 kg
                int pricePerKg = getPricePerKg(type);
                int total = weight * pricePerKg;

                Order o = new Order(i, type, weight, total, System.currentTimeMillis());
                orderList.add(o);
            }
            // Post result to UI thread
            uiHandler.post(() -> adapter.notifyDataSetChanged());
        }).start();
    }

    private int getPricePerKg(String type) {
        if (type.equalsIgnoreCase("Cuci")) return 15000;
        else if (type.equalsIgnoreCase("Cuci + Setrika")) return 15000;
        else if (type.equalsIgnoreCase("Dry Clean")) return 25000;
        else return 15000; // default
    }

    private void openDetail(Order order) {
        Intent i = new Intent(this, DetailActivity.class);
        i.putExtra("orderId", order.id);
        i.putExtra("type", order.type);
        i.putExtra("weight", order.weight);
        i.putExtra("price", order.price);
        i.putExtra("timestamp", order.timestamp);
        startActivity(i);
    }
}

// Order.java (model)
class Order {
    int id, weight, price;
    String type;
    long timestamp;
    Order(int id, String type, int weight, int price, long ts) {
        this.id = id; this.type = type; this.weight = weight;
        this.price = price; this.timestamp = ts;
    }
}

// OrderAdapter.java
class OrderAdapter extends RecyclerView.Adapter<OrderAdapter.VH> {

    interface OnItemClick { void click(Order o); }

    private final List<Order> data;
    private final OnItemClick listener;

    OrderAdapter(List<Order> d, OnItemClick l) { data = d; listener = l; }

    @Override public VH onCreateViewHolder(ViewGroup p, int vt) {
        View v = LayoutInflater.from(p.getContext())
                .inflate(android.R.layout.simple_list_item_2, p, false);
        return new VH(v);
    }
    @Override public void onBindViewHolder(VH h, int pos) {
        Order o = data.get(pos);
        h.title.setText("Order #" + o.id + " – " + o.type);
        h.sub.setText(o.weight + " kg – Rp " + o.price);
