// PatientListFragment.java
package com.example.antrianclinic;

import android.os.Bundle;
import android.os.Handler;
import android.os.Looper;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.TextView;
import androidx.annotation.NonNull;
import androidx.annotation.Nullable;
import androidx.fragment.app.Fragment;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;
import java.util.ArrayList;
import java.util.List;
import java.util.concurrent.Executor;
import java.util.concurrent.Executors;

public class PatientListFragment extends Fragment {

    private RecyclerView rvPatients;
    private PatientAdapter adapter;
    private TextView tvStatus;
    private Handler uiHandler = new Handler(Looper.getMainLooper());
    private Executor executor = Executors.newSingleThreadExecutor();

    @Nullable
    @Override
    public View onCreateView(@NonNull LayoutInflater inflater,
                             @Nullable ViewGroup container,
                             @Nullable Bundle savedInstanceState) {
        View view = inflater.inflate(R.layout.fragment_patient_list, container, false);
        rvPatients = view.findViewById(R.id.rv_patients);
        tvStatus   = view.findViewById(R.id.tv_status);

        rvPatients.setLayoutManager(new LinearLayoutManager(getContext()));
        adapter = new PatientAdapter(new ArrayList<Patient>());
        rvPatients.setAdapter(adapter);

        // Tampilkan pesan loading setelah 2 detik
        uiHandler.postDelayed(new Runnable() {
            @Override
            public void run() {
                tvStatus.setText("Memuat data...");
            }
        }, 2000);

        // Mulai proses berat di background thread
        executor.execute(new Runnable() {
            @Override
            public void run() {
                final List<Patient> patients = generatePatients(); // simulasi beban

                // Kembalikan hasil ke UI thread
                uiHandler.post(new Runnable() {
                    @Override
                    public void run() {
                        tvStatus.setText(""); // bersihkan status
                        adapter.updateData(patients);
                    }
                });
            }
        });

        return view;
    }

    // Simulasi proses berat: buat 30 pasien, tiap pasien delay 200ms
    private List<Patient> generatePatients() {
        List<Patient> list = new ArrayList<>();
        String[] names = {"Ahmad", "Budi", "Citra", "Dedi", "Eka",
                          "Fajar", "Gita", "Hadi", "Indra", "Joko"};
        for (int i = 0; i < 30; i++) {
            try {
                Thread.sleep(200); // beban kerja
            } catch (InterruptedException e) {
                e.printStackTrace();
            }
            String name = names[i % names.length] + " " + (i + 1);
            int estimate = 3 + (i % 5); // estimasi menit
            list.add(new Patient(name, estimate));
        }
        return list;
    }
}
```

