// PatientAdapter.java
package com.example.antrianclinic;

import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.TextView;
import androidx.annotation.NonNull;
import androidx.recyclerview.widget.RecyclerView;
import java.util.List;

public class PatientAdapter extends RecyclerView.Adapter<PatientAdapter.PatientVH> {

    private List<Patient> patients;

    public PatientAdapter(List<Patient> patients) {
        this.patients = patients;
    }

    public void updateData(List<Patient> newPatients) {
        this.patients = newPatients;
        notifyDataSetChanged();
    }

    @NonNull
    @Override
    public PatientVH onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {
        View v = LayoutInflater.from(parent.getContext())
                .inflate(R.layout.item_patient, parent, false);
        return new PatientVH(v);
    }

    @Override
    public void onBindViewHolder(@NonNull PatientVH holder, int position) {
        Patient p = patients.get(position);
        holder.tvName.setText(p.getName());
        holder.tvEstimate.setText(p.getEstimate() + " menit");
    }

    @Override
    public int getItemCount() {
        return patients.size();
    }

    static class PatientVH extends RecyclerView.ViewHolder {
        TextView tvName, tvEstimate;
        PatientVH(@NonNull View itemView) {
            super(itemView);
            tvName = itemView.findViewById(R.id.tv_name);
            tvEstimate = itemView.findViewById(R.id.tv_estimate);
        }
    }
}
