// MainActivity.java
package com.example.infokampus;

import android.os.Bundle;
import androidx.annotation.NonNull;
import androidx.appcompat.app.AppCompatActivity;
import androidx.fragment.app.Fragment;
import com.google.android.material.bottomnavigation.BottomNavigationView;

public class MainActivity extends AppCompatActivity {

    private BottomNavigationView bottomNav;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_main);   // layout berisi FrameLayout + BottomNavigationView

        bottomNav = findViewById(R.id.bottom_navigation);
        // Tampilkan FormFragment pertama kali
        loadFragment(new FormFragment());

        bottomNav.setOnNavigationItemSelectedListener(
            new BottomNavigationView.OnNavigationItemSelectedListener() {
                @Override
                public boolean onNavigationItemSelected(@NonNull android.view.MenuItem item) {
                    Fragment selectedFragment;
                    if (item.getItemId() == R.id.nav_form) {
                        selectedFragment = new FormFragment();
                    } else {
                        // ProfileFragment akan menerima data lewat Bundle yang disimpan di MainActivity
                        selectedFragment = new ProfileFragment();
                        // Ambil data yang terakhir disimpan (jika ada)
                        Bundle data = getIntent().getExtras();
                        if (data != null) {
                            selectedFragment.setArguments(data);
                        }
                    }
                    loadFragment(selectedFragment);
                    return true;
                }
            });
    }

    private void loadFragment(Fragment fragment) {
        getSupportFragmentManager()
                .beginTransaction()
                .replace(R.id.fragment_container, fragment)
                .commit();
    }
}
