package com.example.infomahasiswa;

import android.content.Intent;
import android.os.Bundle;
import android.os.Handler;
import androidx.appcompat.app.AppCompatActivity;
import androidx.fragment.app.Fragment;
import androidx.fragment.app.FragmentTransaction;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.Button;
import android.widget.TextView;
import java.util.ArrayList;

// ---------- SplashActivity ----------
public class SplashActivity extends AppCompatActivity {
    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_splash);

        // Delay 2 detik lalu pindah ke MainActivity
        new Handler().postDelayed(() -> {
            Intent i = new Intent(SplashActivity.this, MainActivity.class);
            startActivity(i);
            finish();
        }, 2000);
    }
}

// ---------- MainActivity ----------
public class MainActivity extends AppCompatActivity {

    // Simulasi data mahasiswa
    public static final ArrayList<Mahasiswa> mahasiswaList = new ArrayList<>();

    static {
        mahasiswaList.add(new Mahasiswa("Ahmad Fauzi", "21010101", "Teknik Informatika", "2021"));
        mahasiswaList.add(new Mahasiswa("Budi Santoso", "21010102", "Teknik Informatika", "2021"));
        mahasiswaList.add(new Mahasiswa("Citra Lestari", "21010103", "Sistem Informasi", "2021"));
        // Tambahkan data lain bila diperlukan
    }

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_main);
        // Tampilkan ListFragment pertama kali
        loadFragment(new ListFragment());
    }

    public boolean loadFragment(Fragment fragment) {
        if (fragment != null) {
            getSupportFragmentManager()
                .beginTransaction()
                .replace(R.id.fragment_container, fragment)
                .commit();
            return true;
        }
        return false;
    }
}

// ---------- Model Mahasiswa ----------
class Mahasiswa {
    String nama, nim, jurusan, angkatan;
    Mahasiswa(String n, String i, String j, String a) {
        nama = n; nim = i; jurusan = j; angkatan = a;
    }
}

// ---------- ListFragment ----------
class ListFragment extends Fragment {

    private RecyclerView recyclerView;
    private MahasiswaAdapter adapter;

    @Override
    public View onCreateView(LayoutInflater inflater, ViewGroup container,
                             Bundle savedInstanceState) {
        return inflater.inflate(R.layout.fragment_list, container, false);
    }

    @Override
    public void onViewCreated(View view, Bundle savedInstanceState) {
        recyclerView = view.findViewById(R.id.recycler_view);
        recyclerView.setLayoutManager(new LinearLayoutManager(getContext()));
        adapter = new MahasiswaAdapter(MainActivity.mahasiswaList);
        recyclerView.setAdapter(adapter);
    }

    // Adapter untuk RecyclerView
    class MahasiswaAdapter extends RecyclerView.Adapter<MahasiswaAdapter.VH> {

        private final ArrayList<Mahasiswa> data;

        MahasiswaAdapter(ArrayList<Mahasiswa> d) { data = d; }

        @Override
        public VH onCreateViewHolder(ViewGroup parent, int viewType) {
            View v = LayoutInflater.from(parent.getContext())
                    .inflate(R.layout.item_mahasiswa, parent, false);
            return new VH(v);
        }

        @Override
        public void onBindViewHolder(VH holder, int position) {
            Mahasiswa m = data.get(position);
            holder.tvNama.setText(m.nama);
            holder.tvNim.setText(m.nim);
            holder.itemView.setOnClickListener(v -> {
                // Kirim data ke DetailFragment via Bundle
                Bundle b = new Bundle();
                b.putString("nama", m.nama);
                b.putString("nim", m.nim);
                b.putString("jurusan", m.jurusan);
                b.putString("angkatan", m.angkatan);
                DetailFragment df = new DetailFragment();
                df.setArguments(b);
                ((MainActivity) getActivity()).loadFragment(df);
            });
        }

        @Override
        public int getItemCount() { return data.size(); }

        class VH extends RecyclerView.ViewHolder {
            TextView tvNama, tvNim;
            VH(View itemView) {
                super(itemView);
                tvNama = itemView.findViewById(R.id.tv_nama);
                tvNim  = itemView.findViewById(R.id.tv_nim);
            }
        }
    }
}

// ---------- DetailFragment ----------
class DetailFragment extends Fragment {

    private TextView tvNama, tvNim, tvJurusan, tvAngkatan;
    private Button btnBack;

    @Override
    public View onCreateView(LayoutInflater inflater, ViewGroup container,
                             Bundle savedInstanceState) {
        return inflater.inflate(R.layout.fragment_detail, container, false);
    }

    @Override
    public void onViewCreated(View view, Bundle savedInstanceState) {
        tvNama    = view.findViewById(R.id.tv_detail_nama);
        tvNim     = view.findViewById(R.id.tv_detail_nim);
        tvJurusan = view.findViewById(R.id.tv_detail_jurusan);
        tvAngkatan= view.findViewById(R.id.tv_detail_angkatan);
        btnBack   = view.findViewById(R.id.btn_back);

        // Ambil data dari Bundle
        Bundle b = getArguments();
        if (b != null) {
            tvNama.setText(b.getString("nama"));
            tvNim.setText(b.getString("nim"));
            tvJurusan.setText(b.getString("jurusan"));
            tvAngkatan.setText(b.getString("angkatan"));
        }

        btnBack.setOnClickListener(v -> {
            // Kembali ke ListFragment
            ((MainActivity) getActivity()).loadFragment(new ListFragment());
        });
    }
}
```

**Catatan UI (untuk screenshot):**  

- `activity_splash.xml` – `ImageView` menampilkan logo di tengah.  
- `activity_main.xml` – `FrameLayout` dengan id `fragment_container`.  
- `fragment_list.xml` – `RecyclerView` penuh layar.  
- `item_mahasiswa.xml` – `LinearLayout` horizontal dengan dua `TextView` (nama & NIM).  
- `fragment_detail.xml` – `LinearLayout` vertikal menampilkan empat `TextView` (nama, NIM, jurusan, angkatan) dan satu `Button` “Kembali”.  

Aplikasi ini hanya memakai **Activity**, **Intent**, **RecyclerView**, dan **Fragment** sesuai modul yang telah diajarkan.

