```java (android)
package com.example.infomahasiswa;

import android.os.Bundle;
import android.view.MenuItem;
import android.view.View;
import android.widget.TextView;
import androidx.annotation.NonNull;
import androidx.appcompat.app.AppCompatActivity;
import androidx.fragment.app.Fragment;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;
import com.google.android.material.bottomnavigation.BottomNavigationView;
import java.util.ArrayList;
import java.util.List;

/* ---------- MainActivity ---------- */
public class MainActivity extends AppCompatActivity {

    private BottomNavigationView bottomNav;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_main);

        bottomNav = findViewById(R.id.bottom_navigation);
        bottomNav.setOnNavigationItemSelectedListener(item -> {
            Fragment selected = null;
            switch (item.getItemId()) {
                case R.id.nav_home:
                    selected = new HomeFragment();
                    break;
                case R.id.nav_profile:
                    selected = new ProfileFragment(); // kosong, hanya untuk tampilan default
                    break;
                case R.id.nav_settings:
                    selected = new SettingsFragment();
                    break;
            }
            return loadFragment(selected);
        });

        // tampilkan HomeFragment pertama kali
        bottomNav.setSelectedItemId(R.id.nav_home);
    }

    private boolean loadFragment(Fragment fragment) {
        if (fragment != null) {
            getSupportFragmentManager()
                .beginTransaction()
                .replace(R.id.fragment_container, fragment)
                .commit();
            return true;
        }
        return false;
    }

    // dipanggil dari HomeFragment ketika item dipilih
    public void openProfile(String nama, String nim) {
        ProfileFragment pf = new ProfileFragment();
        pf.setMahasiswaData(nama, nim);
        loadFragment(pf);
        bottomNav.setSelectedItemId(R.id.nav_profile);
    }
}

/* ---------- HomeFragment ---------- */
class HomeFragment extends Fragment {

    private RecyclerView rv;
    private List<Mahasiswa> list = new ArrayList<>();

    @Override
    public View onCreateView(@NonNull android.view.LayoutInflater inflater,
                             android.view.ViewGroup container,
                             Bundle savedInstanceState) {
        View v = inflater.inflate(R.layout.fragment_home, container, false);
        rv = v.findViewById(R.id.recycler_view);
        rv.setLayoutManager(new LinearLayoutManager(getContext()));
        populateData();
        MahasiswaAdapter adapter = new MahasiswaAdapter(list, (m) -> {
            // kirim data ke MainActivity
            ((MainActivity) getActivity()).openProfile(m.nama, m.nim);
        });
        rv.setAdapter(adapter);
        return v;
    }

    private void populateData() {
        list.add(new Mahasiswa("Ani", "210101"));
        list.add(new Mahasiswa("Budi", "210102"));
        list.add(new Mahasiswa("Citra", "210103"));
        list.add(new Mahasiswa("Dedi", "210104"));
        list.add(new Mahasiswa("Eka", "210105"));
    }
}

/* ---------- ProfileFragment ---------- */
class ProfileFragment extends Fragment {

    private String nama = "";
    private String nim = "";
    private TextView tvInfo, tvLog;
    private StringBuilder logBuilder = new StringBuilder();

    public void setMahasiswaData(String nama, String nim) {
        this.nama = nama;
        this.nim = nim;
    }

    @Override
    public void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        logBuilder.append("onCreate()\n");
    }

    @Override
    public View onCreateView(@NonNull android.view.LayoutInflater inflater,
                             android.view.ViewGroup container,
                             Bundle savedInstanceState) {
        View v = inflater.inflate(R.layout.fragment_profile, container, false);
        tvInfo = v.findViewById(R.id.tv_info);
        tvLog = v.findViewById(R.id.tv_log);
        tvInfo.setText("Nama : " + nama + "\nNIM  : " + nim);
        tvLog.setText(logBuilder.toString());
        return v;
    }

    @Override
    public void onStart() {
        super.onStart();
        logBuilder.append("onStart()\n");
    }

    @Override
    public void onResume() {
        super.onResume();
        logBuilder.append("onResume()\n");
        tvLog.setText(logBuilder.toString());
    }

    @Override
    public void onPause() {
        super.onPause();
        logBuilder.append("onPause()\n");
    }

    @Override
    public void onStop() {
        super.onStop();
        logBuilder.append("onStop()\n");
    }

    @Override
    public void onDestroyView() {
        super.onDestroyView();
        logBuilder.append("onDestroyView()\n");
    }
}

/* ---------- SettingsFragment ---------- */
class SettingsFragment extends Fragment {
    @Override
    public View onCreateView(@NonNull android.view.LayoutInflater inflater,
                             android.view.ViewGroup container,
                             Bundle savedInstanceState) {
        View v = inflater.inflate(R.layout.fragment_settings, container, false);
        ((TextView) v.findViewById(R.id.tv_settings)).setText("Pengaturan aplikasi");
        return v;
    }
}

/* ---------- Model & Adapter ---------- */
class Mahasiswa {
    String nama, nim;
    Mahasiswa(String n, String i) { nama = n; nim = i; }
}

class MahasiswaAdapter extends RecyclerView.Adapter<MahasiswaAdapter.VH> {

    interface OnItemClick { void onClick(Mahasiswa m); }
    private List<Mahasiswa> data;
    private OnItemClick listener;

    MahasiswaAdapter(List<Mahasiswa> d, OnItemClick l) { data = d; listener = l; }

    class VH extends RecyclerView.ViewHolder {
        TextView tv;
        VH(View itemView) {
            super(itemView);
            tv = itemView.findViewById(R.id.item_text);
            itemView.setOnClickListener(v -> listener.onClick(data.get(getAdapterPosition())));
        }
    }

    @NonNull @Override public VH onCreateViewHolder(@NonNull android.view.ViewGroup parent, int viewType) {
        View v = android.view.LayoutInflater.from(parent.getContext())
                .inflate(R.layout.item_mahasiswa, parent, false);
        return new VH(v);
    }

    @Override public void onBindViewHolder(@NonNull VH holder, int position) {
        Mahasiswa m = data.get(position);
        holder.tv.setText(m.nama + " - " + m.nim);
    }

    @Override public int getItemCount() { return data.size(); }
}
```

