// DetailActivity.java
package com.example.travelexplorer;

import android.content.Intent;
import android.net.Uri;
import android.os.Bundle;
import android.widget.Button;
import android.widget.TextView;
import android.widget.Toast;
import androidx.appcompat.app.AppCompatActivity;

public class DetailActivity extends AppCompatActivity {

    private static final String EXTRA_NAMA = "extra_nama";
    private static final String EXTRA_HARGA = "extra_harga";

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_detail);

        TextView tvNama = findViewById(R.id.tvNama);
        TextView tvHarga = findViewById(R.id.tvHarga);
        Button btnMap = findViewById(R.id.btnMap);

        String nama = getIntent().getStringExtra(EXTRA_NAMA);
        int harga = getIntent().getIntExtra(EXTRA_HARGA, 0);

        tvNama.setText("Paket: " + nama);
        tvHarga.setText("Harga: Rp " + String.format("%,d", harga));

        btnMap.setOnClickListener(v -> openMap(nama));
    }

    private void openMap(String nama) {
        double lat, lng;
        switch (nama) {
            case "Bali":      lat = -8.3405; lng = 115.0920; break;
            case "Yogyakarta":lat = -7.7956; lng = 110.3695; break;
            case "Lombok":    lat = -8.5833; lng = 116.1167; break;
            default:          lat = 0; lng = 0;
        }
        String uri = "geo:" + lat + "," + lng + "?q=" + Uri.encode(nama);
        Intent mapIntent = new Intent(Intent.ACTION_VIEW, Uri.parse(uri));

        if (mapIntent.resolveActivity(getPackageManager()) != null) {
            startActivity(mapIntent);
        } else {
            Toast.makeText(this, "Aplikasi peta tidak tersedia", Toast.LENGTH_SHORT).show();
        }
    }
}

