```java (android)
package com.example.kopikita;

import android.app.Activity;
import android.os.Bundle;
import android.os.Handler;
import android.os.Looper;
import android.widget.Toast;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;
import org.json.JSONArray;
import org.json.JSONObject;
import java.io.BufferedReader;
import java.io.InputStreamReader;
import java.net.HttpURLConnection;
import java.net.URL;
import java.util.ArrayList;

// Model class
class User {
    String firstName, lastName, email;
    User(String f, String l, String e) {
        firstName = f; lastName = l; email = e;
    }
}

// Adapter for RecyclerView
class UserAdapter extends RecyclerView.Adapter<UserAdapter.VH> {
    private final ArrayList<User> data;
    UserAdapter(ArrayList<User> d) { data = d; }

    static class VH extends RecyclerView.ViewHolder {
        android.widget.TextView tvName, tvEmail;
        VH(android.view.View v) {
            super(v);
            tvName = v.findViewById(android.R.id.text1);
            tvEmail = v.findViewById(android.R.id.text2);
        }
    }

    @Override public VH onCreateViewHolder(android.view.ViewGroup p, int t) {
        android.view.View v = android.view.LayoutInflater.from(p.getContext())
                .inflate(android.R.layout.simple_list_item_2, p, false);
        return new VH(v);
    }

    @Override public void onBindViewHolder(VH h, int p) {
        User u = data.get(p);
        h.tvName.setText(u.firstName + " " + u.lastName);
        h.tvEmail.setText(u.email);
    }

    @Override public int getItemCount() { return data.size(); }
}

// Main Activity
public class MainActivity extends Activity {
    private RecyclerView rv;
    private final ArrayList<User> users = new ArrayList<>();
    private final Handler uiHandler = new Handler(Looper.getMainLooper());

    @Override protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        // Simple layout programmatically
        rv = new RecyclerView(this);
        rv.setLayoutManager(new LinearLayoutManager(this));
        rv.setAdapter(new UserAdapter(users));
        setContentView(rv);

        fetchData();
    }

    private void fetchData() {
        new Thread(() -> {
            try {
                URL url = new URL("https://reqres.in/api/users?page=1");
                HttpURLConnection conn = (HttpURLConnection) url.openConnection();
                conn.setRequestMethod("GET");
                conn.connect();

                BufferedReader br = new BufferedReader(
                        new InputStreamReader(conn.getInputStream()));
                StringBuilder sb = new StringBuilder();
                String line;
                while ((line = br.readLine()) != null) sb.append(line);
                br.close();

                JSONObject root = new JSONObject(sb.toString());
                JSONArray arr = root.getJSONArray("data");
                for (int i = 0; i < arr.length(); i++) {
                    JSONObject obj = arr.getJSONObject(i);
                    String fn = obj.getString("first_name");
                    String ln = obj.getString("last_name");
                    String em = obj.getString("email");
                    users.add(new User(fn, ln, em));
                }

                uiHandler.post(() -> rv.getAdapter().notifyDataSetChanged());
            } catch (Exception e) {
                uiHandler.post(() ->
                        Toast.makeText(MainActivity.this,
                                "Gagal mengambil data", Toast.LENGTH_SHORT).show());
            }
        }).start();
    }
}
```

