// UserListFragment.java
package com.example.reqresapp;

import android.os.Bundle;
import android.os.Handler;
import android.os.Looper;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.Toast;
import androidx.annotation.NonNull;
import androidx.annotation.Nullable;
import androidx.fragment.app.Fragment;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;
import org.json.JSONArray;
import org.json.JSONObject;
import java.io.BufferedReader;
import java.io.InputStreamReader;
import java.net.HttpURLConnection;
import java.net.URL;
import java.util.ArrayList;

public class UserListFragment extends Fragment {

    private RecyclerView recyclerView;
    private UserAdapter adapter;
    private final ArrayList<User> userList = new ArrayList<>();

    @Nullable
    @Override
    public View onCreateView(@NonNull LayoutInflater inflater,
                             @Nullable ViewGroup container,
                             @Nullable Bundle savedInstanceState) {
        View view = inflater.inflate(R.layout.fragment_user_list, container, false);
        recyclerView = view.findViewById(R.id.recycler_view);
        recyclerView.setLayoutManager(new LinearLayoutManager(getContext()));
        adapter = new UserAdapter(userList, getContext());
        recyclerView.setAdapter(adapter);
        fetchUsers();                     // Mulai request jaringan
        return view;
    }

    /** Mengambil data pengguna secara background thread */
    private void fetchUsers() {
        new Thread(() -> {
            try {
                URL url = new URL("https://reqres.in/api/users?page=1");
                HttpURLConnection conn = (HttpURLConnection) url.openConnection();
                conn.setRequestMethod("GET");
                conn.setConnectTimeout(8000);
                conn.setReadTimeout(8000);

                int responseCode = conn.getResponseCode();
                if (responseCode == HttpURLConnection.HTTP_OK) {
                    BufferedReader br = new BufferedReader(
                            new InputStreamReader(conn.getInputStream()));
                    StringBuilder sb = new StringBuilder();
                    String line;
                    while ((line = br.readLine()) != null) {
                        sb.append(line);
                    }
                    br.close();
                    parseJson(sb.toString());
                } else {
                    postError("Server error: " + responseCode);
                }
                conn.disconnect();
            } catch (Exception e) {
                postError("Gagal mengambil data. Periksa koneksi internet.");
            }
        }).start();
    }

    /** Parsing JSON dan mengisi userList */
    private void parseJson(String jsonStr) {
        try {
            JSONObject obj = new JSONObject(jsonStr);
            JSONArray data = obj.getJSONArray("data");
            for (int i = 0; i < data.length(); i++) {
                JSONObject u = data.getJSONObject(i);
                User user = new User(
                        u.getInt("id"),
                        u.getString("email"),
                        u.getString("first_name"),
                        u.getString("last_name"),
                        u.getString("avatar")
                );
                userList.add(user);
            }
            // Update UI pada thread utama
            new Handler(Looper.getMainLooper()).post(() -> adapter.notifyDataSetChanged());
        } catch (Exception e) {
            postError("Parsing error.");
        }
    }

    /** Menampilkan toast pada UI thread */
    private void postError(String msg) {
        new Handler(Looper.getMainLooper()).post(() ->
                Toast.makeText(getContext(), msg, Toast.LENGTH_LONG).show());
    }
}
