public class UserAdapter extends RecyclerView.Adapter<UserAdapter.UserVH> {

    private final ArrayList<User> users;
    private final Context ctx;

    public UserAdapter(ArrayList<User> users, Context ctx) {
        this.users = users;
        this.ctx = ctx;
    }

    @NonNull
    @Override
    public UserVH onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {
        View v = LayoutInflater.from(ctx).inflate(R.layout.item_user, parent, false);
        return new UserVH(v);
    }

    @Override
    public void onBindViewHolder(@NonNull UserVH holder, int position) {
        User u = users.get(position);
        holder.tvName.setText(u.getFullName());
        Picasso.get().load(u.avatar).into(holder.ivAvatar);

        holder.itemView.setOnClickListener(v -> {
            Intent i = new Intent(ctx, DetailActivity.class);
            i.putExtra("id", u.id);
            i.putExtra("email", u.email);
            i.putExtra("firstName", u.firstName);
            i.putExtra("lastName", u.lastName);
            i.putExtra("avatar", u.avatar);
            ctx.startActivity(i);
        });
    }

    @Override
    public int getItemCount() {
        return users.size();
    }

    static class UserVH extends RecyclerView.ViewHolder {
        ImageView ivAvatar;
        TextView tvName;

        UserVH(@NonNull View itemView) {
            super(itemView);
            ivAvatar = itemView.findViewById(R.id.iv_avatar);
            tvName   = itemView.findViewById(R.id.tv_name);
        }
    }
}
