```java
// MainActivity.java
package com.example.travelmate;

import android.app.Activity;
import android.content.Intent;
import android.os.Bundle;
import android.os.Handler;
import android.os.Looper;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;
import android.view.View;
import android.widget.ProgressBar;
import org.json.JSONArray;
import org.json.JSONObject;
import java.io.BufferedReader;
import java.io.InputStreamReader;
import java.net.HttpURLConnection;
import java.net.URL;
import java.util.ArrayList;

// Model class
class User {
    int id;
    String firstName, lastName, email, avatar;
    User(int id, String f, String l, String e, String a) {
        this.id=id; firstName=f; lastName=l; email=e; avatar=a;
    }
    String fullName() { return firstName + " " + lastName; }
}

// Adapter for RecyclerView
class UserAdapter extends RecyclerView.Adapter<UserAdapter.VH> {
    interface OnItemClick { void click(User u); }
    private ArrayList<User> list; private OnItemClick listener;
    UserAdapter(ArrayList<User> l, OnItemClick c){list=l;listener=c;}
    class VH extends RecyclerView.ViewHolder {
        android.widget.TextView tvName, tvEmail;
        VH(View v){ super(v);
            tvName=v.findViewById(android.R.id.text1);
            tvEmail=v.findViewById(android.R.id.text2);
            v.setOnClickListener(vv->listener.click(list.get(getAdapterPosition())));
        }
    }
    @Override public VH onCreateViewHolder(android.view.ViewGroup p,int t){
        View v=android.view.LayoutInflater.from(p.getContext())
                .inflate(android.R.layout.simple_list_item_2,p,false);
        return new VH(v);
    }
    @Override public void onBindViewHolder(VH h,int p){
        User u=list.get(p);
        h.tvName.setText(u.fullName());
        h.tvEmail.setText(u.email);
    }
    @Override public int getItemCount(){ return list.size(); }
}

// Main Activity
public class MainActivity extends Activity {
    private RecyclerView rv; private ProgressBar pb;
    private ArrayList<User> users = new ArrayList<>();
    private Handler uiHandler = new Handler(Looper.getMainLooper());

    @Override protected void onCreate(Bundle savedInstanceState){
        super.onCreate(savedInstanceState);
        // Simple layout programmatically
        android.widget.LinearLayout root = new android.widget.LinearLayout(this);
        root.setOrientation(android.widget.LinearLayout.VERTICAL);
        pb = new ProgressBar(this);
        rv = new RecyclerView(this);
        rv.setLayoutManager(new LinearLayoutManager(this));
        root.addView(pb,
                new android.widget.LinearLayout.LayoutParams(
                        android.widget.LinearLayout.LayoutParams.WRAP_CONTENT,
                        android.widget.LinearLayout.LayoutParams.WRAP_CONTENT,1));
        root.addView(rv,
                new android.widget.LinearLayout.LayoutParams(
                        android.widget.LinearLayout.LayoutParams.MATCH_PARENT,
                        android.widget.LinearLayout.LayoutParams.MATCH_PARENT));
        setContentView(root);
        fetchData();
    }

    private void fetchData(){
        pb.setVisibility(View.VISIBLE);
        new Thread(() -> {
            try{
                URL url = new URL("https://reqres.in/api/users?page=1");
                HttpURLConnection conn = (HttpURLConnection)url.openConnection();
                conn.setRequestMethod("GET");
                BufferedReader br = new BufferedReader(
                        new InputStreamReader(conn.getInputStream()));
                StringBuilder sb = new StringBuilder(); String line;
                while((line=br.readLine())!=null) sb.append(line);
                br.close();
                JSONObject root = new JSONObject(sb.toString());
                JSONArray data = root.getJSONArray("data");
                for(int i=0;i<data.length();i++){
                    JSONObject o = data.getJSONObject(i);
                    User u = new User(
                            o.getInt("id"),
                            o.getString("first_name"),
                            o.getString("last_name"),
                            o.getString("email"),
                            o.getString("avatar"));
                    users.add(u);
                }
                uiHandler.post(this::setupRecycler);
            }catch(Exception e){
                e.printStackTrace();
                uiHandler.post(()->pb.setVisibility(View.GONE));
            }
        }).start();
    }

    private void setupRecycler(){
        pb.setVisibility(View.GONE);
        UserAdapter adapter = new UserAdapter(users, user -> {
            Intent i = new Intent(MainActivity.this, DetailActivity.class);
            i.putExtra("id", user.id);
            i.putExtra("first", user.firstName);
            i.putExtra("last", user.lastName);
            i.putExtra("email", user.email);
            i.putExtra("avatar", user.avatar);
            startActivity(i);
        });
        rv.setAdapter(adapter);
    }
}
