// DetailFragment.java
package com.example.travelmate;

import android.os.Bundle;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.graphics.Bitmap;
import android.graphics.BitmapFactory;
import androidx.fragment.app.Fragment;
import java.io.InputStream;
import java.net.HttpURLConnection;
import java.net.URL;
import android.os.Handler;
import android.os.Looper;

public class DetailFragment extends Fragment {
    private android.widget.ImageView ivAvatar;
    private android.widget.TextView tvName, tvEmail;
    private Handler ui = new Handler(Looper.getMainLooper());

    @Override public View onCreateView(LayoutInflater inf, ViewGroup parent,
                                       Bundle savedInstanceState){
        // Simple vertical layout
        android.widget.LinearLayout root = new android.widget.LinearLayout(getContext());
        root.setOrientation(android.widget.LinearLayout.VERTICAL);
        ivAvatar = new android.widget.ImageView(getContext());
        tvName   = new android.widget.TextView(getContext());
        tvEmail  = new android.widget.TextView(getContext());

        root.addView(ivAvatar,
                new android.widget.LinearLayout.LayoutParams(
                        android.widget.LinearLayout.LayoutParams.MATCH_PARENT,400));
        root.addView(tvName);
        root.addView(tvEmail);
        return root;
    }

    @Override public void onActivityCreated(Bundle savedInstanceState){
        super.onActivityCreated(savedInstanceState);
        Bundle a = getArguments();
        if(a!=null){
            String first = a.getString("first");
            String last  = a.getString("last");
            String email = a.getString("email");
            String avatarUrl = a.getString("avatar");
            tvName.setText("Nama Lengkap: "+first+" "+last);
            tvEmail.setText("Email: "+email);
            loadAvatar(avatarUrl);
        }
    }

    private void loadAvatar(String urlStr){
        new Thread(() -> {
            try{
                URL url = new URL(urlStr);
                HttpURLConnection conn = (HttpURLConnection)url.openConnection();
                conn.setDoInput(true);
                conn.connect();
                InputStream is = conn.getInputStream();
                Bitmap bmp = BitmapFactory.decodeStream(is);
                ui.post(() -> ivAvatar.setImageBitmap(bmp));
            }catch(Exception e){ e.printStackTrace(); }
        }).start();
    }
}
