package com.example.catatanhari;

import android.content.Context;
import android.content.SharedPreferences;
import android.os.Bundle;
import android.view.View;
import android.widget.Button;
import android.widget.EditText;
import android.widget.Switch;
import android.widget.Toast;
import androidx.appcompat.app.AppCompatActivity;

public class MainActivity extends AppCompatActivity {

    private static final String PREF_NAME = "UserPref";
    private static final String KEY_NAME = "username";
    private static final String KEY_DARK = "dark_mode";

    private EditText etName;
    private Switch swDark;
    private Button btnSave;
    private SharedPreferences sharedPref;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_main);

        etName = findViewById(R.id.etName);
        swDark = findViewById(R.id.swDark);
        btnSave = findViewById(R.id.btnSave);

        sharedPref = getSharedPreferences(PREF_NAME, Context.MODE_PRIVATE);
        loadPreferences();

        btnSave.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View v) {
                savePreferences();
            }
        });
    }

    private void loadPreferences() {
        String savedName = sharedPref.getString(KEY_NAME, "");
        boolean isDark = sharedPref.getBoolean(KEY_DARK, false);

        etName.setText(savedName);
        swDark.setChecked(isDark);
    }

    private void savePreferences() {
        String name = etName.getText().toString().trim();
        boolean darkMode = swDark.isChecked();

        SharedPreferences.Editor editor = sharedPref.edit();
        editor.putString(KEY_NAME, name);
        editor.putBoolean(KEY_DARK, darkMode);
        editor.apply();   // asynchronous commit

        Toast.makeText(this, "Data tersimpan", Toast.LENGTH_SHORT).show();
    }
}
```

