// MainActivity.java
public class MainActivity extends AppCompatActivity
        implements SharedPreferences.OnSharedPreferenceChangeListener {

    private RecyclerView rv;
    private TextView tvFav;
    private SharedPreferences prefs;
    private final String[] drinks = {"Espresso","Latte","Cappuccino","Americano","Mocha"};

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        applyTheme();                         // 1. terapkan tema dari pref
        setContentView(R.layout.activity_main);

        prefs = getSharedPreferences("KopiPrefs", MODE_PRIVATE);
        prefs.registerOnSharedPreferenceChangeListener(this);

        tvFav = findViewById(R.id.tvFav);
        rv    = findViewById(R.id.rvDrinks);
        rv.setLayoutManager(new LinearLayoutManager(this));
        rv.setAdapter(new DrinkAdapter());

        updateFavorite();                    // 2. tampilkan favorit bila ada
    }

    private void applyTheme() {
        SharedPreferences sp = getSharedPreferences("KopiPrefs", MODE_PRIVATE);
        boolean dark = sp.getBoolean("dark_mode", false);
        setTheme(dark ? R.style.AppTheme_Dark : R.style.AppTheme_Light);
    }

    private void updateFavorite() {
        String fav = prefs.getString("favorite_drink", "Tidak ada");
        tvFav.setText("Favorit: " + fav);
    }

    @Override
    public void onSharedPreferenceChanged(SharedPreferences sharedPreferences, String key) {
        if (key.equals("dark_mode")) {
            recreate();                      // refresh UI dengan tema baru
        } else if (key.equals("favorite_drink")) {
            updateFavorite();
        }
    }

    // Adapter internal
    private class DrinkAdapter extends RecyclerView.Adapter<DrinkAdapter.VH> {
        class VH extends RecyclerView.ViewHolder {
            TextView tv;
            VH(View v) { super(v); tv = v.findViewById(R.id.tvItem); }
        }
        @NonNull @Override public VH onCreateViewHolder(@NonNull ViewGroup p, int vt) {
            View v = LayoutInflater.from(p.getContext())
                    .inflate(R.layout.item_drink, p, false);
            return new VH(v);
        }
        @Override public void onBindViewHolder(@NonNull VH h, int pos) {
            h.tv.setText(drinks[pos]);
            h.itemView.setOnClickListener(v -> {
                Intent i = new Intent(MainActivity.this, DetailActivity.class);
                i.putExtra("drink", drinks[pos]);
                startActivity(i);
            });
        }
        @Override public int getItemCount() { return drinks.length; }
    }

    // Menu untuk membuka SettingsActivity
    @Override public boolean onCreateOptionsMenu(Menu menu) {
        getMenuInflater().inflate(R.menu.main_menu, menu);
        return true;
    }
    @Override public boolean onOptionsItemSelected(@NonNull MenuItem item) {
        if (item.getItemId() == R.id.menu_settings) {
            startActivity(new Intent(this, SettingsActivity.class));
            return true;
        }
        return super.onOptionsItemSelected(item);
    }
}

