package com.example.pustakacerdas;

import android.content.Context;
import android.content.Intent;
import android.content.SharedPreferences;
import android.graphics.Color;
import android.os.Bundle;
import android.preference.PreferenceManager;
import android.view.Menu;
import android.view.MenuItem;
import android.widget.Toast;
import androidx.annotation.NonNull;
import androidx.appcompat.app.AppCompatActivity;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;
import java.util.ArrayList;
import java.util.List;

public class MainActivity extends AppCompatActivity
        implements SharedPreferences.OnSharedPreferenceChangeListener {

    private RecyclerView rvBooks;
    private BookAdapter adapter;
    private List<Book> bookList = new ArrayList<>();
    private SharedPreferences sp;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        // load theme before setContentView
        sp = PreferenceManager.getDefaultSharedPreferences(this);
        applyTheme(sp.getString("theme_mode", "light"));
        setContentView(R.layout.activity_main);

        rvBooks = findViewById(R.id.rvBooks);
        rvBooks.setLayoutManager(new LinearLayoutManager(this));
        adapter = new BookAdapter(bookList, sp, this);
        rvBooks.setAdapter(adapter);

        // fetch data di background thread (simulasi network)
        new Thread(new Runnable() {
            @Override
            public void run() {
                simulateNetworkDelay();
                final List<Book> fetched = mockFetchBooks();
                runOnUiThread(new Runnable() {
                    @Override
                    public void run() {
                        bookList.clear();
                        bookList.addAll(fetched);
                        adapter.notifyDataSetChanged();
                    }
                });
            }
        }).start();

        // register listener
        sp.registerOnSharedPreferenceChangeListener(this);
    }

    private void simulateNetworkDelay() {
        try { Thread.sleep(1500); } catch (InterruptedException e) { }
    }

    private List<Book> mockFetchBooks() {
        List<Book> list = new ArrayList<>();
        list.add(new Book(1, "Android Programming"));
        list.add(new Book(2, "Data Structures"));
        list.add(new Book(3, "Machine Learning"));
        return list;
    }

    private void applyTheme(String mode) {
        if ("dark".equals(mode)) {
            setTheme(R.style.AppTheme_Dark);
        } else {
            setTheme(R.style.AppTheme_Light);
        }
    }

    @Override
    public boolean onCreateOptionsMenu(Menu menu) {
        getMenuInflater().inflate(R.menu.main_menu, menu); // menu berisi item Settings
        return true;
    }

    @Override
    public boolean onOptionsItemSelected(@NonNull MenuItem item) {
        if (item.getItemId() == R.id.action_settings) {
            startActivity(new Intent(this, SettingsActivity.class));
            return true;
        }
        return super.onOptionsItemSelected(item);
    }

    // -----------------------------------------------------------
    // Listener untuk perubahan SharedPreferences
    // -----------------------------------------------------------
    @Override
    public void onSharedPreferenceChanged(SharedPreferences sharedPreferences, String key) {
        if ("theme_mode".equals(key)) {
            // ubah tema secara dinamis
            applyTheme(sharedPreferences.getString(key, "light"));
            // recreate agar style diterapkan
            recreate();
        } else if ("notifications_enabled".equals(key)) {
            boolean enabled = sharedPreferences.getBoolean(key, true);
            String msg = enabled ? "Notifikasi diaktifkan" : "Notifikasi dimatikan";
            Toast.makeText(this, msg, Toast.LENGTH_SHORT).show();
        }
    }

    @Override
    protected void onDestroy() {
        super.onDestroy();
        sp.unregisterOnSharedPreferenceChangeListener(this);
    }
}

