package com.example.pustakacerdas;

import android.content.Context;
import android.content.SharedPreferences;
import android.graphics.Color;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.ImageButton;
import android.widget.TextView;
import androidx.annotation.NonNull;
import androidx.recyclerview.widget.RecyclerView;
import java.util.List;

class BookAdapter extends RecyclerView.Adapter<BookAdapter.BookVH> {

    private List<Book> books;
    private SharedPreferences sp;
    private Context ctx;

    BookAdapter(List<Book> books, SharedPreferences sp, Context ctx) {
        this.books = books;
        this.sp = sp;
        this.ctx = ctx;
    }

    @NonNull
    @Override
    public BookVH onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {
        View v = LayoutInflater.from(ctx).inflate(R.layout.item_book, parent, false);
        return new BookVH(v);
    }

    @Override
    public void onBindViewHolder(@NonNull BookVH holder, int position) {
        final Book b = books.get(position);
        holder.tvTitle.setText(b.title);
        // cek favorit dari SharedPreferences
        boolean fav = sp.getBoolean("fav_" + b.id, false);
        holder.btnFav.setColorFilter(fav ? Color.RED : Color.GRAY);
        holder.btnFav.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View v) {
                boolean nowFav = !sp.getBoolean("fav_" + b.id, false);
                SharedPreferences.Editor ed = sp.edit();
                ed.putBoolean("fav_" + b.id, nowFav);
                ed.apply(); // simpan perubahan
                notifyItemChanged(position);
            }
        });
    }

    @Override
    public int getItemCount() { return books.size(); }

    static class BookVH extends RecyclerView.ViewHolder {
        TextView tvTitle;
        ImageButton btnFav;
        BookVH(@NonNull View itemView) {
            super(itemView);
            tvTitle = itemView.findViewById(R.id.tvTitle);
            btnFav  = itemView.findViewById(R.id.btnFav);
        }
    }
}

