package com.example.pustakacerdas;

import android.content.SharedPreferences;
import android.os.Bundle;
import android.preference.PreferenceManager;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.Switch;
import androidx.annotation.NonNull;
import androidx.annotation.Nullable;
import androidx.fragment.app.Fragment;

public class SettingsFragment extends Fragment {

    private Switch switchTheme, switchNotify;
    private SharedPreferences sp;

    @Nullable
    @Override
    public View onCreateView(@NonNull LayoutInflater inflater,
                             @Nullable ViewGroup container,
                             @Nullable Bundle savedInstanceState) {
        View v = inflater.inflate(R.layout.fragment_settings, container, false);
        sp = PreferenceManager.getDefaultSharedPreferences(getContext());

        switchTheme = v.findViewById(R.id.switch_theme);
        switchNotify = v.findViewById(R.id.switch_notify);

        // set nilai default / yang tersimpan
        switchTheme.setChecked("dark".equals(sp.getString("theme_mode", "light")));
        switchNotify.setChecked(sp.getBoolean("notifications_enabled", true));

        switchTheme.setOnCheckedChangeListener((buttonView, isChecked) -> {
            SharedPreferences.Editor ed = sp.edit();
            ed.putString("theme_mode", isChecked ? "dark" : "light");
            ed.apply(); // perubahan langsung dipantau oleh listener di MainActivity
        });

        switchNotify.setOnCheckedChangeListener((buttonView, isChecked) -> {
            SharedPreferences.Editor ed = sp.edit();
            ed.putBoolean("notifications_enabled", isChecked);
            ed.apply();
        });

        return v;
    }
}
