```java (android)
package com.example.warungpakjaya;

import android.app.AlertDialog;
import android.content.Context;
import android.content.DialogInterface;
import android.database.Cursor;
import android.database.sqlite.SQLiteDatabase;
import android.database.sqlite.SQLiteOpenHelper;
import android.os.AsyncTask;
import android.os.Bundle;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.*;

import androidx.appcompat.app.AppCompatActivity;

public class MainActivity extends AppCompatActivity {

    private EditText etItem, etQty;
    private Button btnAdd;
    private ListView lvSales;
    private SalesAdapter adapter;
    private SalesDbHelper dbHelper;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_main);

        etItem = findViewById(R.id.etItem);
        etQty  = findViewById(R.id.etQty);
        btnAdd = findViewById(R.id.btnAdd);
        lvSales = findViewById(R.id.lvSales);

        dbHelper = new SalesDbHelper(this);
        adapter = new SalesAdapter(this);
        lvSales.setAdapter(adapter);

        loadSales();

        btnAdd.setOnClickListener(v -> {
            String item = etItem.getText().toString().trim();
            String qtyStr = etQty.getText().toString().trim();
            if (!item.isEmpty() && !qtyStr.isEmpty()) {
                int qty = Integer.parseInt(qtyStr);
                new InsertTask(item, qty).execute();
            }
        });

        lvSales.setOnItemLongClickListener((parent, view, position, id) -> {
            SalesItem salesItem = adapter.getItem(position);
            new AlertDialog.Builder(MainActivity.this)
                    .setTitle("Hapus")
                    .setMessage("Hapus \"" + salesItem.itemName + "\"?")
                    .setPositiveButton("Ya", (dialog, which) -> new DeleteTask(salesItem.id).execute())
                    .setNegativeButton("Tidak", null)
                    .show();
            return true;
        });
    }

    private void loadSales() {
        new AsyncTask<Void, Void, Cursor>() {
            @Override
            protected Cursor doInBackground(Void... voids) {
                return dbHelper.getReadableDatabase()
                        .rawQuery("SELECT * FROM sales ORDER BY id ASC", null);
            }

            @Override
            protected void onPostExecute(Cursor cursor) {
                adapter.swapCursor(cursor);
            }
        }.execute();
    }

    // ---------- AsyncTask untuk Insert ----------
    private class InsertTask extends AsyncTask<Void, Void, Void> {
        private final String item;
        private final int qty;

        InsertTask(String item, int qty) {
            this.item = item;
            this.qty = qty;
        }

        @Override
        protected Void doInBackground(Void... voids) {
            SQLiteDatabase db = dbHelper.getWritableDatabase();
            db.execSQL("INSERT INTO sales (item_name, quantity) VALUES (?, ?)",
                    new Object[]{item, qty});
            return null;
        }

        @Override
        protected void onPostExecute(Void aVoid) {
            etItem.setText("");
            etQty.setText("");
            loadSales();
        }
    }

    // ---------- AsyncTask untuk Delete ----------
    private class DeleteTask extends AsyncTask<Void, Void, Void> {
        private final int id;

        DeleteTask(int id) {
            this.id = id;
        }

        @Override
        protected Void doInBackground(Void... voids) {
            SQLiteDatabase db = dbHelper.getWritableDatabase();
            db.execSQL("DELETE FROM sales WHERE id = ?", new Object[]{id});
            return null;
        }

        @Override
        protected void onPostExecute(Void aVoid) {
            loadSales();
        }
    }

    // ---------- Model ----------
    private static class SalesItem {
        int id;
        String itemName;
        int quantity;

        SalesItem(int id, String itemName, int quantity) {
            this.id = id;
            this.itemName = itemName;
            this.quantity = quantity;
        }
    }

    // ---------- Adapter ----------
    private static class SalesAdapter extends CursorAdapter {

        private LayoutInflater inflater;

        SalesAdapter(Context context) {
            super(context, null, 0);
            inflater = LayoutInflater.from(context);
        }

        @Override
        public View newView(Context context, Cursor cursor, ViewGroup parent) {
            return inflater.inflate(android.R.layout.simple_list_item_1, parent, false);
        }

        @Override
        public void bindView(View view, Context context, Cursor cursor) {
            TextView tv = (TextView) view.findViewById(android.R.id.text1);
            String name = cursor.getString(cursor.getColumnIndexOrThrow("item_name"));
            int qty = cursor.getInt(cursor.getColumnIndexOrThrow("quantity"));
            tv.setText(name + " – " + qty);
        }

        SalesItem getItem(int position) {
            Cursor c = getCursor();
            if (c.moveToPosition(position)) {
                int id = c.getInt(c.getColumnIndexOrThrow("id"));
                String name = c.getString(c.getColumnIndexOrThrow("item_name"));
                int qty = c.getInt(c.getColumnIndexOrThrow("quantity"));
                return new SalesItem(id, name, qty);
            }
            return null;
        }

        void swapCursor(Cursor newCursor) {
            super.swapCursor(newCursor);
            notifyDataSetChanged();
        }
    }

    // ---------- SQLiteOpenHelper ----------
    private static class SalesDbHelper extends SQLiteOpenHelper {
        private static final String DB_NAME = "warung.db";
        private static final int DB_VER = 1;

        SalesDbHelper(Context ctx) {
            super(ctx, DB_NAME, null, DB_VER);
        }

        @Override
        public void onCreate(SQLiteDatabase db) {
            db.execSQL("CREATE TABLE sales (" +
                    "id INTEGER PRIMARY KEY AUTOINCREMENT, " +
                    "item_name TEXT, " +
                    "quantity INTEGER)");
        }

        @Override
        public void onUpgrade(SQLiteDatabase db, int oldVer, int newVer) {
            db.execSQL("DROP TABLE IF EXISTS sales");
            onCreate(db);
        }
    }
}
```

