// DBHelper.java
public class DBHelper extends SQLiteOpenHelper {
    private static final String DB_NAME = "kopi.db";
    private static final int DB_VER = 1;
    private static final String TBL = "transactions";

    public DBHelper(Context ctx) { super(ctx, DB_NAME, null, DB_VER); }

    @Override public void onCreate(SQLiteDatabase db) {
        db.execSQL("CREATE TABLE " + TBL +
                "(id INTEGER PRIMARY KEY AUTOINCREMENT," +
                "customer TEXT, coffee TEXT, qty INTEGER)");
    }
    @Override public void onUpgrade(SQLiteDatabase db,int oldV,int newV){ db.execSQL("DROP TABLE IF EXISTS "+TBL); onCreate(db); }

    public long insert(String cust,String coffee,int qty){
        ContentValues cv = new ContentValues();
        cv.put("customer",cust); cv.put("coffee",coffee); cv.put("qty",qty);
        return getWritableDatabase().insert(TBL,null,cv);
    }
    public Cursor all(){ return getReadableDatabase().query(TBL,null,null,null,null,null,null); }
}

// MainActivity.java
public class MainActivity extends AppCompatActivity {
    private DBHelper db;
    private RecyclerView rv;
    private List<String> list = new ArrayList<>();
    private ArrayAdapter<String> adapter; // simple adapter for demo

    @Override protected void onCreate(Bundle savedInstanceState){
        super.onCreate(savedInstanceState);
        LinearLayout root = new LinearLayout(this);
        root.setOrientation(LinearLayout.VERTICAL);
        rv = new RecyclerView(this);
        rv.setLayoutManager(new LinearLayoutManager(this));
        adapter = new ArrayAdapter<>(this, android.R.layout.simple_list_item_1, list);
        rv.setAdapter(new RecyclerView.Adapter<RecyclerView.ViewHolder>(){
            @Override public int getItemCount(){ return list.size(); }
            @Override public RecyclerView.ViewHolder onCreateViewHolder(ViewGroup p,int vt){
                TextView tv = new TextView(MainActivity.this);
                tv.setPadding(16,16,16,16);
                return new RecyclerView.ViewHolder(tv){};
            }
            @Override public void onBindViewHolder(RecyclerView.ViewHolder h,int pos){
                ((TextView)h.itemView).setText(list.get(pos));
            }
        });
        Button addBtn = new Button(this);
        addBtn.setText("Tambah Transaksi");
        addBtn.setOnClickListener(v->showInputDialog());
        root.addView(rv, new LinearLayout.LayoutParams(
                ViewGroup.LayoutParams.MATCH_PARENT,0,1));
        root.addView(addBtn);
        setContentView(root);
        db = new DBHelper(this);
        loadData();
    }

    private void loadData(){
        list.clear();
        Cursor c = db.all();
        while(c.moveToNext()){
            String row = (c.getInt(0))+". "+c.getString(1)+" | "+
                         c.getString(2)+" | "+c.getInt(3)+" gelas";
            list.add(row);
        }
        c.close();
        rv.getAdapter().notifyDataSetChanged();
    }

    private void showInputDialog(){
        LinearLayout form = new LinearLayout(this);
        form.setOrientation(LinearLayout.VERTICAL);
        EditText etCust = new EditText(this); etCust.setHint("Nama Pelanggan");
        EditText etCoffee = new EditText(this); etCoffee.setHint("Jenis Kopi");
        EditText etQty = new EditText(this); etQty.setHint("Jumlah Gelas");
        etQty.setInputType(InputType.TYPE_CLASS_NUMBER);
        form.addView(etCust);
        form.addView(etCoffee);
        form.addView(etQty);
        new AlertDialog.Builder(this)
            .setTitle("Tambah Transaksi")
            .setView(form)
            .setNegativeButton("Batal",null)
            .setPositiveButton("Simpan",(d,which)->{
                String cust = etCust.getText().toString().trim();
                String coffee = etCoffee.getText().toString().trim();
                int qty = Integer.parseInt(etQty.getText().toString().trim());
                db.insert(cust,coffee,qty);
                loadData();
            }).show();
    }
}
