**KUNCI JAWABAN:**  
```java (android)
package com.example.exploreindonesia;

import android.app.*;
import android.content.*;
import android.database.*;
import android.database.sqlite.*;
import android.os.*;
import android.view.*;
import android.widget.*;
import androidx.annotation.*;
import androidx.fragment.app.*;
import androidx.recyclerview.widget.*;
import org.json.*;

public class MainActivity extends Activity {
    private TourDatabaseHelper dbHelper;
    private SharedPreferences prefs;
    private static final String PREF_CAT = "fav_category";

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        dbHelper = new TourDatabaseHelper(this);
        prefs = getSharedPreferences("user_prefs", MODE_PRIVATE);
        setContentView(R.layout.activity_main);
        // load fragment
        getSupportFragmentManager()
                .beginTransaction()
                .replace(R.id.container, new TourListFragment())
                .commit();

        // start background thread for fetch & store
        new Thread(new FetchAndStoreTask()).start();
    }

    /*** Background thread ***/
    class FetchAndStoreTask implements Runnable {
        @Override public void run() {
            int budget = 0;
            // ----- while loop : validate budget input -----
            while (budget <= 0) {
                // Simulate dialog input (replace with real UI in practice)
                budget = simulateUserBudgetInput(); // returns int
            }

            // ----- simulate network fetch (JSON string) -----
            String json = simulateJsonFromServer();

            try {
                JSONArray arr = new JSONArray(json);
                int stored = 0;
                // ----- for loop : iterate JSON objects -----
                for (int i = 0; i < arr.length(); i++) {
                    JSONObject obj = arr.getJSONObject(i);
                    int price = obj.getInt("price");
                    // ----- continue : skip if price > budget -----
                    if (price > budget) continue;
                    // ----- break : stop after 20 entries -----
                    if (stored >= 20) break;

                    // insert into SQLite
                    SQLiteDatabase db = dbHelper.getWritableDatabase();
                    ContentValues cv = new ContentValues();
                    cv.put("name", obj.getString("name"));
                    cv.put("category", obj.getString("category"));
                    cv.put("price", price);
                    cv.put("available", obj.getInt("available"));
                    db.insert("tours", null, cv);
                    db.close();
                    stored++;
                }

                // ----- nested loop : calculate subtotal per category -----
                SQLiteDatabase db = dbHelper.getReadableDatabase();
                Cursor cur = db.rawQuery(
                        "SELECT category, SUM(price) FROM tours GROUP BY category", null);
                while (cur.moveToNext()) {
                    String cat = cur.getString(0);
                    int sum = cur.getInt(1);
                    // (could be stored in prefs or UI)
                }
                cur.close(); db.close();

            } catch (JSONException e) { e.printStackTrace(); }
        }

        // -----------------------------------------------------------------
        // Dummy methods to keep the example self‑contained
        private int simulateUserBudgetInput() {
            // In real app, use AlertDialog with EditText
            return 750000; // fixed for illustration
        }
        private String simulateJsonFromServer() {
            return "[{\"name\":\"Pulau Komodo\",\"category\":\"Alam\",\"price\":450000,\"available\":1}," +
                   "{\"name\":\"Danau Toba\",\"category\":\"Alam\",\"price\":300000,\"available\":1}," +
                   "{\"name\":\"Candi Borobudur\",\"category\":\"Budaya\",\"price\":600000,\"available\":0}," +
                   "{\"name\":\"Bromo Sunrise\",\"category\":\"Alam\",\"price\":200000,\"available\":1}]";
        }
    }
}

/*** SQLite Helper ***/
class TourDatabaseHelper extends SQLiteOpenHelper {
    private static final String DB_NAME = "tours.db";
    private static final int DB_VER = 1;
    TourDatabaseHelper(Context ctx){ super(ctx, DB_NAME, null, DB_VER); }
    @Override public void onCreate(SQLiteDatabase db) {
        db.execSQL("CREATE TABLE tours(" +
                "id INTEGER PRIMARY KEY AUTOINCREMENT," +
                "name TEXT," +
                "category TEXT," +
                "price INTEGER," +
                "available INTEGER)");
    }
    @Override public void onUpgrade(SQLiteDatabase db,int oldV,int newV){}
}

/*** Fragment with RecyclerView ***/
class TourListFragment extends Fragment {
    private RecyclerView rv;
    private TourAdapter adapter;
    private SharedPreferences prefs;
    private static final String PREF_CAT = "fav_category";

    @Nullable @Override
    public View onCreateView(@NonNull LayoutInflater inf, @Nullable ViewGroup parent,
                             @Nullable Bundle savedInstanceState) {
        View v = inf.inflate(R.layout.fragment_tour_list, parent, false);
        rv = v.findViewById(R.id.recycler);
        rv.setLayoutManager(new LinearLayoutManager(getContext()));
        prefs = getActivity().getSharedPreferences("user_prefs", Context.MODE_PRIVATE);
        loadData();
        return v;
    }

    private void loadData() {
        String favCat = prefs.getString(PREF_CAT, "Alam");
        SQLiteDatabase db = new TourDatabaseHelper(getContext()).getReadableDatabase();
        Cursor cur = db.rawQuery(
                "SELECT id,name,price,available FROM tours WHERE category=?",
                new String[]{favCat});
        adapter = new TourAdapter(cur);
        rv.setAdapter(adapter);
        cur.close(); db.close();
    }
}

/*** RecyclerView Adapter ***/
class TourAdapter extends RecyclerView.Adapter<TourAdapter.VH> {
    private Cursor cursor;
    TourAdapter(Cursor c){ this.cursor = c; }

    class VH extends RecyclerView.ViewHolder {
        TextView tvName, tvPrice, tvDisc;
        VH(View item){ super(item);
            tvName = item.findViewById(R.id.name);
            tvPrice = item.findViewById(R.id.price);
            tvDisc = item.findViewById(R.id.disc);
        }
    }

    @NonNull @Override public VH onCreateViewHolder(@NonNull ViewGroup p, int t) {
        View v = LayoutInflater.from(p.getContext())
                .inflate(R.layout.item_tour, p, false);
        return new VH(v);
    }

    @Override public void onBindViewHolder(@NonNull VH h, int pos) {
        if (!cursor.moveToPosition(pos)) return;
        int id = cursor.getInt(0);
        String name = cursor.getString(1);
        int price = cursor.getInt(2);
        int avail = cursor.getInt(3);

        h.tvName.setText(name);
        h.tvPrice.setText("IDR " + price);
        // ----- if‑else & nested if for discount label -----
        if (avail == 1) {
            if (price < 500000) h.tvDisc.setText("Diskon 10%");
            else h.tvDisc.setText("Tidak Diskon");
        } else {
            h.tvDisc.setText("Tidak Tersedia");
        }

        // ----- Intent to DetailActivity -----
        h.itemView.setOnClickListener(v -> {
            Intent i = new Intent(v.getContext(), DetailActivity.class);
            i.putExtra("tour_id", id);
            v.getContext().startActivity(i);
        });
    }

    @Override public int getItemCount() { return cursor.getCount(); }
}

/*** Detail Activity ***/
class DetailActivity extends Activity {
    @Override protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_detail);
        int tourId = getIntent().getIntExtra("tour_id", -1);
        if (tourId == -1) finish();

        SQLiteDatabase db = new TourDatabaseHelper(this).getReadableDatabase();
        Cursor cur = db.rawQuery(
                "SELECT name,category,price,available FROM tours WHERE id=?",
                new String[]{String.valueOf(tourId)});
        if (cur.moveToFirst()) {
            String name = cur.getString(0);
            String cat = cur.getString(1);
            int price = cur.getInt(2);
            int avail = cur.getInt(3);

            ((TextView)findViewById(R.id.tvName)).setText(name);
            ((TextView)findViewById(R.id.tvCat)).setText(cat);
            ((TextView)findViewById(R.id.tvPrice)).setText("IDR " + price);
            ((TextView)findViewById(R.id.tvAvail)).setText(avail==1?"Ya":"Tidak");

            // ----- nested if for discount display -----
            if (avail == 1) {
                if (price < 500000) {
                    ((TextView)findViewById(R.id.tvDisc))
                            .setText("DISKON 10%");
                } else {
                    ((TextView)findViewById(R.id.tvDisc))
                            .setText("Tidak ada diskon");
                }
            } else {
                ((TextView)findViewById(R.id.tvDisc))
                        .setText("Tidak tersedia");
            }
        }
        cur.close(); db.close();
    }
}
