if ($result && mysqli_num_rows($result) > 0) {
    echo "<h2>Data Penjualan</h2>";
    echo "<table><tr><th>No</th><th>Nama Pelanggan</th><th>Jenis Kopi</th><th>Qty</th><th>Harga</th><th>Subtotal</th></tr>";
    $no = 1;
    $grand = 0;
    while ($row = mysqli_fetch_assoc($result)) {
        $subtotal = $row["qty"] * $row["harga"];
        $grand   += $subtotal;
        echo "<tr>
                <td>{$no}</td>
                <td>{$row['nama']}</td>
                <td>{$row['kopi']}</td>
                <td>{$row['qty']}</td>
                <td>".number_format($row['harga'])."</td>
                <td>".number_format($subtotal)."</td>
              </tr>";
        $no++;
    }
    echo "</table>";
    echo "<p><strong>Grand Total: ".number_format($grand)."</strong></p>";
} else {
    echo "<p>Belum ada data penjualan.</p>";
}
mysqli_close($conn);
?>
</body>
</html>
```

