<?php
/* ---------- Koneksi Database ---------- */
$host='localhost'; $user='root'; $pass=''; $db='warung_segar';
$conn=mysqli_connect($host,$user,$pass,$db);
if(!$conn){
    die('Koneksi gagal: '.mysqli_connect_error());
}

/* ---------- Buat tabel jika belum ada ---------- */
$sql="CREATE TABLE IF NOT EXISTS barang(
        id INT AUTO_INCREMENT PRIMARY KEY,
        nama VARCHAR(100),
        kategori VARCHAR(30),
        harga INT,
        stok INT
      ) ENGINE=InnoDB;";
mysqli_query($conn,$sql);

/* ---------- Simpan data dari form ---------- */
if($_SERVER['REQUEST_METHOD']==='POST'){
    $nama   = mysqli_real_escape_string($conn,$_POST['nama']);
    $kat    = mysqli_real_escape_string($conn,$_POST['kategori']);
    $harga  = (int)$_POST['harga'];
    $stok   = (int)$_POST['stok'];
    $ins    = "INSERT INTO barang (nama,kategori,harga,stok) VALUES
               ('$nama','$kat',$harga,$stok)";
    mysqli_query($conn,$ins);
}

/* ---------- Ambil semua data barang ---------- */
$res    = mysqli_query($conn,"SELECT * FROM barang ORDER BY kategori, id");
$all    = [];
while($row = mysqli_fetch_assoc($res)){
    $all[] = $row;               // simpan ke array untuk loop selanjutnya
}

/* ---------- Definisikan kategori ---------- */
$kategori = ['Makanan','Minuman','Alat','Lainnya'];
?>
