        <label>Kategori</label>
        <select name="kategori" required>
            <?php foreach($kategori as $k){ echo "<option value='$k'>$k</option>"; } ?>
        </select>
        <label>Harga (Rp)</label>
        <input type="number" name="harga" id="price" required>
        <label>Stok</label>
        <input type="number" name="stok" id="stock" required>
        <div id="err" class="error"></div>
        <button type="submit">Tambah Barang</button>
    </form>
</div>

<?php
/* ---------- Tampilkan data per kategori ---------- */
$displayed = 0;                     // hitung total baris yang sudah ditampilkan
foreach($kategori as $kat){         // for‑loop kategori
    echo "<h3>Kategori: $kat</h3>";
    echo "<table><tr><th>No</th><th>Nama Barang</th><th>Harga (Rp)</th><th>Stok</th><th>Total Harga</th></tr>";
    $i = 1;
    $idx = 0;
    $len = count($all);
    while($idx < $len){            // while‑loop data barang
        $b = $all[$idx];
        $idx++;
        if($b['kategori'] !== $kat) continue;   // skip barang bukan kategori ini
        if($b['stok'] == 0) continue;           // continue bila stok 0
        if($displayed >= 10){ break 2; }        // break semua loop setelah 10 baris
        $class = ($b['stok'] < 5) ? 'low-stock' : '';
        $total = $b['harga'] * $b['stok'];
        echo "<tr class='$class'>
                <td>{$i}</td>
                <td>{$b['nama']}</td>
                <td>{$b['harga']}</td>
                <td>{$b['stok']}</td>
                <td>{$total}</td>
              </tr>";
        $i++; $displayed++;
    }
    echo "</table>";
}
mysqli_close($conn);
?>
</body>
</html>
```

