**KUNCI JAWABAN:**
```html
<!DOCTYPE html>
<html lang="id">
<head>
    <meta charset="UTF-8">
    <title>Sistem Pemesanan Tiket Bioskop</title>
    <style>
        body {font-family: Arial, sans-serif; margin: 20px;}
        #log {background:#f4f4f4; padding:10px; white-space:pre-wrap;}
    </style>
</head>
<body>
<h2>Bioskop Cahaya Film</h2>
<pre id="log"></pre>

<script>
/* 1. Data film dan kursi */
var films = {
    "Avengers":   {price:80000, seats: []},
    "Inception":  {price:75000, seats: []},
    "Parasite":   {price:70000, seats: []}
};

/* inisialisasi kursi 5x5 (false = kosong) */
for (var f in films) {
    for (var i = 0; i < 5; i++) {
        films[f].seats[i] = [];
        for (var j = 0; j < 5; j++) {
            films[f].seats[i][j] = false;
        }
    }
}

/* helper untuk menulis ke log */
function writeLog(txt){ document.getElementById('log').textContent += txt + "\n"; }

/* 2. Menu utama */
while (true) {
    writeLog("\n=== Selamat datang di Bioskop Cahaya Film ===");
    writeLog("Menu:\n1. Lihat Daftar Film\n2. Pesan Tiket\n3. Keluar");
    var choice = prompt("Pilih menu (1/2/3):");
    if (choice===null) break;   // user menutup prompt
    switch (choice) {
        case "1":   // Lihat Daftar Film
            writeLog("\nDaftar Film:");
            for (var key in films) {
                writeLog("- " + key + ": Harga Rp " + films[key].price);
            }
            break;

        case "2":   // Pesan Tiket
            var filmName = prompt("Masukkan nama film:");
            if (filmName===null) break;
            if (!films.hasOwnProperty(filmName)) {
                alert("Film tidak ditemukan!");
                break;
            }

            var qty = parseInt(prompt("Masukkan jumlah tiket:"),10);
            if (isNaN(qty) || qty<=0) { alert("Jumlah tiket tidak valid!"); break; }

            /* 3. Hitung diskon */
            var discount = 0;
            if (qty >= 5) {
                discount = 0.10;
            } else if (qty >= 3) {
                discount = 0.05;
            } else {
                discount = 0;
            }

            /* 4. Tampilkan kursi */
            writeLog("\nKursi tersedia (X = terisi, O = kosong):");
            var seatMap = films[filmName].seats;
            for (var r = 0; r < seatMap.length; r++) {
                var line = "";
                for (var c = 0; c < seatMap[r].length; c++) {
                    if (seatMap[r][c]) { line += "X "; continue; }
                    line += "O ";
                }
                writeLog(line.trim());
            }

            /* 5. Pilih kursi (validasi dengan while) */
            var row, col;
            while (true) {
                row = parseInt(prompt("Pilih baris kursi (1‑5):"),10) - 1;
                col = parseInt(prompt("Pilih kolom kursi (1‑5):"),10) - 1;
                if (isNaN(row)||isNaN(col)||row<0||row>4||col<0||col>4) {
                    alert("Koordinat tidak valid, coba lagi.");
                    continue;
                }
                if (seatMap[row][col]) {
                    alert("Kursi sudah terisi, pilih kursi lain.");
                    continue;
                }
                break;   // kursi valid dan kosong
            }

            /* tandai kursi terisi */
            seatMap[row][col] = true;

            /* 6. Hitung total */
            var total = films[filmName].price * qty;
            total = total - (total * discount);
            alert("Tiket berhasil dipesan!\nTotal harga setelah diskon: Rp " + total);
            writeLog("\nTiket untuk " + filmName + " berhasil dipesan (" + qty + " tiket).");
            writeLog("Total setelah diskon (" + (discount*100) + "%): Rp " + total);
            break;

        case "3":   // Keluar
            writeLog("\nTerima kasih, sampai jumpa!");
            break;

        default:
            alert("Pilihan tidak dikenal!");
    }
    if (choice==="3") break;   // keluar dari while utama
}
</script>
</body>
</html>
```

