```php
<?php
/* ---------- config ---------- */
$dsn = 'sqlite:' . __DIR__ . '/inventory.db';
$pdo = new PDO($dsn);
$pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);
$pdo->exec("CREATE TABLE IF NOT EXISTS products (
    id INTEGER PRIMARY KEY AUTOINCREMENT,
    name TEXT NOT NULL,
    price INTEGER NOT NULL,
    stock INTEGER NOT NULL
)");

/* ---------- Model ---------- */
class ProductModel {
    private $pdo;
    public function __construct($pdo){ $this->pdo = $pdo; }

    public function all(){
        $stmt = $this->pdo->query("SELECT * FROM products ORDER BY id ASC");
        return $stmt->fetchAll(PDO::FETCH_ASSOC);
    }
    public function find($id){
        $stmt = $this->pdo->prepare("SELECT * FROM products WHERE id = ?");
        $stmt->execute([$id]);
        return $stmt->fetch(PDO::FETCH_ASSOC);
    }
    public function create($data){
        $stmt = $this->pdo->prepare("INSERT INTO products (name,price,stock) VALUES (?,?,?)");
        return $stmt->execute([$data['name'],$data['price'],$data['stock']]);
    }
    public function update($id,$data){
        $stmt = $this->pdo->prepare("UPDATE products SET name=?,price=?,stock=? WHERE id=?");
        return $stmt->execute([$data['name'],$data['price'],$data['stock'],$id]);
    }
    public function delete($id){
        $stmt = $this->pdo->prepare("DELETE FROM products WHERE id=?");
        return $stmt->execute([$id]);
    }
}

/* ---------- Controller ---------- */
class ProductController {
    private $model;
    public function __construct($model){ $this->model = $model; }

    public function list(){
        $products = $this->model->all();
        include 'view_list.php';
    }

    public function add(){
        $error = '';
        if($_SERVER['REQUEST_METHOD']==='POST'){
            $name = trim($_POST['name']);
            $price = $_POST['price'];
            $stock = $_POST['stock'];
            if($name===''||!ctype_digit($price)||!ctype_digit($stock)|| $price<=0||$stock<0){
                $error = 'Error: Nama tidak boleh kosong, Harga dan Stok harus angka positif.';
            }else{
                $this->model->create(['name'=>$name,'price'=>$price,'stock'=>$stock]);
                header('Location: ?action=list');
                exit;
            }
        }
        include 'view_form.php';
    }

    public function edit(){
        $id = $_GET['id'] ?? 0;
        $product = $this->model->find($id);
        $error = '';
        if(!$product){ header('Location: ?action=list'); exit; }
        if($_SERVER['REQUEST_METHOD']==='POST'){
            $name = trim($_POST['name']);
            $price = $_POST['price'];
            $stock = $_POST['stock'];
            if($name===''||!ctype_digit($price)||!ctype_digit($stock)|| $price<=0||$stock<0){
                $error = 'Error: Nama tidak boleh kosong, Harga dan Stok harus angka positif.';
            }else{
                $this->model->update($id,['name'=>$name,'price'=>$price,'stock'=>$stock]);
                header('Location: ?action=list');
                exit;
            }
        }
        include 'view_form.php';
    }

    public function delete(){
        $id = $_GET['id'] ?? 0;
        $this->model->delete($id);
        header('Location: ?action=list');
        exit;
    }
}

/* ---------- Routing ---------- */
$action = $_GET['action'] ?? 'list';
$model   = new ProductModel($pdo);
$controller = new ProductController($model);

switch($action){
    case 'add'   : $controller->add();   break;
    case 'edit'  : $controller->edit();  break;
    case 'delete': $controller->delete();break;
    default      : $controller->list();  break;
}
?>
