namespace App\Http\Controllers;
use App\Models\Book;
use App\Models\Publisher;
use App\Models\Author;
use Illuminate\Http\Request;

class BookController extends Controller
{
    public function index()
    {
        $books = Book::with(['publisher', 'authors'])->get();
        return view('books.index', compact('books'));
    }

    public function create()
    {
        $publishers = Publisher::all();
        $authors    = Author::all();
        return view('books.create', compact('publishers', 'authors'));
    }

    public function store(Request $request)
    {
        $book = Book::create($request->only('title', 'publisher_id'));
        $book->authors()->attach($request->author_id);
        return redirect()->route('books.index');
    }

    public function edit(Book $book)
    {
        $publishers = Publisher::all();
        $authors    = Author::all();
        $selected   = $book->authors->pluck('id')->toArray();
        return view('books.edit', compact('book','publishers','authors','selected'));
    }

    public function update(Request $request, Book $book)
    {
        $book->update($request->only('title', 'publisher_id'));
        $book->authors()->sync($request->author_id);
        return redirect()->route('books.index');
    }

    public function destroy(Book $book)
    {
        $book->authors()->detach();
        $book->delete();
        return redirect()->route('books.index');
    }
}
