namespace App\Http\Controllers;
use App\Models\Patient;
use App\Models\MedicalRecord;
use Illuminate\Http\Request;
use Illuminate\Support\Facades\DB;

class PatientController extends Controller {
    public function index() {
        $patients = Patient::with('medicalRecord')->get();
        return view('patients.index', compact('patients'));
    }

    public function create() {
        return view('patients.create');
    }

    public function store(Request $request) {
        DB::transaction(function () use ($request) {
            $patient = Patient::create($request->only('name','age'));
            $patient->medicalRecord()->create($request->only('record_number','allergy','last_visit'));
        });
        return redirect()->route('patients.index');
    }

    public function edit(Patient $patient) {
        $patient->load('medicalRecord');
        return view('patients.edit', compact('patient'));
    }

    public function update(Request $request, Patient $patient) {
        DB::transaction(function () use ($request, $patient) {
            $patient->update($request->only('name','age'));
            $patient->medicalRecord->update($request->only('record_number','allergy','last_visit'));
        });
        return redirect()->route('patients.index');
    }

    public function destroy(Patient $patient) {
        $patient->delete(); // cascade deletes medical_record
        return redirect()->route('patients.index');
    }

    public function show(Patient $patient) {
        $patient->load('medicalRecord');
        return view('patients.show', compact('patient'));
    }
}

