```php
<?php
// start session untuk menyimpan riwayat pembayaran
session_start();

// inisialisasi array pembayaran jika belum ada
if (!isset($_SESSION['payments'])) {
    $_SESSION['payments'] = [];
}

// update cookie kunjungan
$visit = isset($_COOKIE['visit_count']) ? (int)$_COOKIE['visit_count'] + 1 : 1;
setcookie('visit_count', $visit, time() + 86400 * 30); // 30 hari

$nama = $usia = $anggota = '';
$total = 0;
$showResult = false;

if ($_SERVER['REQUEST_METHOD'] === 'POST') {
    // sanitasi input
    $nama   = htmlspecialchars($_POST['nama'] ?? '');
    $usia   = (int)($_POST['usia'] ?? 0);
    $anggota = ($_POST['anggota'] ?? 'Tidak') === 'Ya' ? 'Ya' : 'Tidak';

    // tarif dasar
    $tarif = 100000;
    $diskon = 0;

    // diskon usia
    if ($usia < 12) {
        $diskon += 0.20; // 20%
    } elseif ($usia >= 60) {
        $diskon += 0.30; // 30%
    }

    // diskon anggota (setelah diskon usia)
    if ($anggota === 'Ya') {
        $diskon += 0.10; // tambahan 10%
    }

    // hitung total bayar
    $total = $tarif * (1 - $diskon);
    $total = round($total); // bulatkan ke rupiah terdekat

    // simpan ke session
    $_SESSION['payments'][] = $total;
    $showResult = true;
}
?>
