    <?php
    // Inisialisasi variabel untuk menampung nilai input
    $nama = $usia = $layanan = $asuransi = $tes = "";
    $error = "";
    $hasil = "";

    if ($_SERVER["REQUEST_METHOD"] == "POST") {
        // Ambil data POST
        $nama      = trim($_POST["nama"]);
        $usia      = trim($_POST["usia"]);
        $layanan   = $_POST["layanan"];
        $asuransi  = $_POST["asuransi"];
        $tes       = isset($_POST["tes"]) ? trim($_POST["tes"]) : "0";

        // Validasi sederhana
        if ($nama == "" || $usia == "" || $layanan == "" || $asuransi == "") {
            $error = "Semua field wajib diisi.";
        } elseif (!is_numeric($usia) || $usia <= 0) {
            $error = "Usia harus berupa angka positif.";
        } elseif (!is_numeric($tes) || $tes < 0) {
            $error = "Jumlah tes harus angka ≥ 0.";
        } else {
            // Konversi ke integer
            $usia = (int)$usia;
            $tes  = (int)$tes;

            // 1. Tarif dasar
            if ($layanan == "Rawat Inap") {
                $tarifDasar = 2000000;
            } elseif ($layanan == "Rawat Jalan") {
                $tarifDasar = 500000;
            } else { // Pemeriksaan Lab
                $tarifDasar = 300000;
            }

            // 2. Diskon usia
            $diskonUsia = 0;
            if ($usia <= 12) {
                $diskonUsia = $tarifDasar * 30 / 100;
            } elseif ($usia >= 60) {
                $diskonUsia = $tarifDasar * 20 / 100;
            }

            // 3. Tarif setelah diskon usia
            $setelahUsia = $tarifDasar - $diskonUsia;

            // 4. Potongan asuransi
            $potonganAsuransi = 0;
            if ($asuransi == "BPJS") {
                $potonganAsuransi = $setelahUsia * 40 / 100;
            } elseif ($asuransi == "Swasta") {
                $potonganAsuransi = $setelahUsia * 25 / 100;
            } // Tidak ada potongan bila "Tidak Ada"

            // 5. Biaya tes tambahan (hanya bila layanan Lab)
            $biayaTes = 0;
            if ($layanan == "Pemeriksaan Lab") {
                $biayaTes = $tes * 150000;
            }

            // 6. Biaya administrasi tetap
            $biayaAdmin = 50000;

            // 7. Total akhir
            $total = $setelahUsia - $potonganAsuransi + $biayaTes + $biayaAdmin;

            // 8. Buat tabel hasil
            $hasil = "
            <h3>Rincian Tagihan Pasien \"$nama\"</h3>
            <table>
                <tr><th>Komponen</th><th>Jumlah</th></tr>
                <tr><td>Tarif Dasar</td><td>Rp " . number_format($tarifDasar,0,',','.') . "</td></tr>
                <tr><td>Diskon Usia (" . ($diskonUsia>0 ? ($usia<=12?'30%':'20%') : '0%') . ")</td><td>-Rp " . number_format($diskonUsia,0,',','.') . "</td></tr>
                <tr><td>Potongan Asuransi (" . ($asuransi=="BPJS"?"40%" : ($asuransi=="Swasta"?"25%" : "0%")) . ")</td><td>-Rp " . number_format($potonganAsuransi,0,',','.') . "</td></tr>
                <tr><td>Biaya Tes Tambahan</td><td>Rp " . number_format($biayaTes,0,',','.') . "</td></tr>
                <tr><td>Biaya Administrasi</td><td>Rp " . number_format($biayaAdmin,0,',','.') . "</td></tr>
                <tr class='total'><td>TOTAL YANG HARUS DIBAYAR</td><td>Rp " . number_format($total,0,',','.') . "</td></tr>
            </table>";
        }
    }
    ?>

    <?php if ($error != ""): ?>
        <p style="color:red; font-weight:bold;"><?= $error ?></p>
    <?php endif; ?>

    <form method="post" action="">
        <label for="nama">Nama Pasien</label>
        <input type="text" id="nama" name="nama" value="<?= htmlspecialchars($nama) ?>">

        <label for="usia">Usia (tahun)</label>
        <input type="number" id="usia" name="usia" min="1" value="<?= htmlspecialchars($usia) ?>">

        <label for="layanan">Jenis Layanan</label>
        <select id="layanan" name="layanan">
            <option value="">-- Pilih Layanan --</option>
            <option value="Rawat Inap" <?= $layanan=="Rawat Inap"?"selected":"" ?>>Rawat Inap</option>
            <option value="Rawat Jalan" <?= $layanan=="Rawat Jalan"?"selected":"" ?>>Rawat Jalan</option>
            <option value="Pemeriksaan Lab" <?= $layanan=="Pemeriksaan Lab"?"selected":"" ?>>Pemeriksaan Lab</option>
        </select>

        <label for="asuransi">Status Asuransi</label>
        <select id="asuransi" name="asuransi">
            <option value="">-- Pilih Asuransi --</option>
            <option value="BPJS" <?= $asuransi=="BPJS"?"selected":"" ?>>BPJS</option>
            <option value="Swasta" <?= $asuransi=="Swasta"?"selected":"" ?>>Asuransi Swasta</option>
            <option value="Tidak Ada" <?= $asuransi=="Tidak Ada"?"selected":"" ?>>Tidak Ada</option>
        </select>

        <label for="tes">Jumlah Tes Laboratorium Tambahan (hanya bila layanan = Pemeriksaan Lab)</label>
        <input type="number" id="tes" name="tes" min="0" value="<?= htmlspecialchars($tes) ?>">

        <button type="submit" class="btn">Hitung Tagihan</button>
    </form>

    <?= $hasil ?>
